import { Mic, MicOff, Play, Pause, Upload, Download, Settings, Volume2, FileText, Zap } from 'lucide-react';

const AMRDeveloperInterface = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [recordedAudio, setRecordedAudio] = useState(null);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [transcription, setTranscription] = useState('');
  const [audioUrl, setAudioUrl] = useState('');
  const [recordingTime, setRecordingTime] = useState(0);
  const [volume, setVolume] = useState(80);
  const [sampleRate, setSampleRate] = useState(8000);
  const [bitRate, setBitRate] = useState(12200);
  const [isProcessing, setIsProcessing] = useState(false);

  const mediaRecorderRef = useRef(null);
  const audioRef = useRef(null);
  const timerRef = useRef(null);
  const chunksRef = useRef([]);

  // 錄音計時器
  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);
    } else {
      clearInterval(timerRef.current);
    }

    return () => clearInterval(timerRef.current);
  }, [isRecording]);

  // 開始錄音
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          sampleRate: sampleRate,
          channelCount: 1,
          echoCancellation: true,
          noiseSuppression: true
        } 
      });

      mediaRecorderRef.current = new MediaRecorder(stream);
      chunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        chunksRef.current.push(event.data);
      };

      mediaRecorderRef.current.onstop = () => {
        const blob = new Blob(chunksRef.current, { type: 'audio/wav' });
        setRecordedAudio(blob);
        const url = URL.createObjectURL(blob);
        setAudioUrl(url);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
      setRecordingTime(0);
    } catch (error) {
      console.error('錄音失敗:', error);
      alert('無法存取麥克風，請檢查權限設定');
    }
  };

  // 停止錄音
  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  // 播放/暫停音訊
  const togglePlayback = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  // 上傳檔案
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setUploadedFile(file);
      const url = URL.createObjectURL(file);
      setAudioUrl(url);
      setRecordedAudio(null);
    }
  };

  // 下載音訊
  const downloadAudio = () => {
    if (recordedAudio) {
      const url = URL.createObjectURL(recordedAudio);
      const a = document.createElement('a');
      a.href = url;
      a.download = `recording_${Date.now()}.wav`;
      a.click();
    }
  };

  // 模擬語音轉文字
  const simulateTranscription = async () => {
    setIsProcessing(true);
    setTranscription('處理中...');

    // 模擬 API 呼叫延遲
    await new Promise(resolve => setTimeout(resolve, 2000));

    const mockTranscriptions = [
      '您好，這是一段測試語音轉文字的範例。',
      '今天天氣很好，適合外出走走。',
      '語音識別技術在近年來有了顯著的進步。',
      '這個 AMR 開發者界面提供了完整的音訊處理功能。',
      '人工智慧與機器學習正在改變我們的生活方式。',
      '歡迎使用這個語音轉文字的開發者工具！'
    ];

    const randomTranscription = mockTranscriptions[Math.floor(Math.random() * mockTranscriptions.length)];
    setTranscription(randomTranscription);
    setIsProcessing(false);
  };

  // 格式化時間
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 p-6">
      <div className="max-w-4xl mx-auto">
        {/* 標題 */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">AMR 開發者界面</h1>
          <p className="text-gray-300">自動語音識別 · 開發工具</p>
        </div>

        {/* 主要控制區域 */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          {/* 錄音控制 */}
          <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
            <h2 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
              <Mic className="w-5 h-5" />
              錄音控制
            </h2>

            <div className="space-y-4">
              <div className="text-center">
                <div className="text-3xl font-mono text-white mb-2">
                  {formatTime(recordingTime)}
                </div>
                <div className="text-sm text-gray-300">
                  {isRecording ? '錄音中...' : '待機中'}
                </div>
              </div>

              <div className="flex justify-center gap-4">
                <button
                  onClick={isRecording ? stopRecording : startRecording}
                  className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium transition-all ${
                    isRecording 
                      ? 'bg-red-500 hover:bg-red-600 text-white' 
                      : 'bg-blue-500 hover:bg-blue-600 text-white'
                  }`}
                >
                  {isRecording ? <MicOff className="w-5 h-5" /> : <Mic className="w-5 h-5" />}
                  {isRecording ? '停止錄音' : '開始錄音'}
                </button>

                {audioUrl && (
                  <button
                    onClick={togglePlayback}
                    className="flex items-center gap-2 px-6 py-3 bg-green-500 hover:bg-green-600 text-white rounded-lg font-medium transition-all"
                  >
                    {isPlaying ? <Pause className="w-5 h-5" /> : <Play className="w-5 h-5" />}
                    {isPlaying ? '暫停' : '播放'}
                  </button>
                )}
              </div>
            </div>
          </div>

          {/* 檔案控制 */}
          <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
            <h2 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
              <Upload className="w-5 h-5" />
              檔案處理
            </h2>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  上傳音訊檔案
                </label>
                <input
                  type="file"
                  accept="audio/*"
                  onChange={handleFileUpload}
                  className="block w-full text-sm text-gray-300 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-purple-500 file:text-white hover:file:bg-purple-600 file:cursor-pointer"
                />
              </div>

              {uploadedFile && (
                <div className="text-sm text-gray-300">
                  已上傳: {uploadedFile.name}
                </div>
              )}

              <div className="flex gap-2">
                <button
                  onClick={downloadAudio}
                  disabled={!recordedAudio}
                  className="flex items-center gap-2 px-4 py-2 bg-indigo-500 hover:bg-indigo-600 disabled:bg-gray-600 text-white rounded-lg text-sm font-medium transition-all"
                >
                  <Download className="w-4 h-4" />
                  下載
                </button>

                <button
                  onClick={simulateTranscription}
                  disabled={!audioUrl || isProcessing}
                  className="flex items-center gap-2 px-4 py-2 bg-orange-500 hover:bg-orange-600 disabled:bg-gray-600 text-white rounded-lg text-sm font-medium transition-all"
                >
                  <Zap className="w-4 h-4" />
                  轉換文字
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* 設定區域 */}
        <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20 mb-8">
          <h2 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
            <Settings className="w-5 h-5" />
            音訊設定
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                取樣率 (Hz)
              </label>
              <select
                value={sampleRate}
                onChange={(e) => setSampleRate(Number(e.target.value))}
                className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value={8000}>8000 Hz</option>
                <option value={16000}>16000 Hz</option>
                <option value={44100}>44100 Hz</option>
                <option value={48000}>48000 Hz</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                位元率 (bps)
              </label>
              <select
                value={bitRate}
                onChange={(e) => setBitRate(Number(e.target.value))}
                className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value={4750}>4.75 kbps</option>
                <option value={5150}>5.15 kbps</option>
                <option value={5900}>5.90 kbps</option>
                <option value={6700}>6.70 kbps</option>
                <option value={7400}>7.40 kbps</option>
                <option value={7950}>7.95 kbps</option>
                <option value={10200}>10.2 kbps</option>
                <option value={12200}>12.2 kbps</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                音量 ({volume}%)
              </label>
              <div className="flex items-center gap-2">
                <Volume2 className="w-4 h-4 text-gray-300" />
                <input
                  type="range"
                  min="0"
                  max="100"
                  value={volume}
                  onChange={(e) => setVolume(Number(e.target.value))}
                  className="flex-1 h-2 bg-white/20 rounded-lg appearance-none slider"
                />
              </div>
            </div>
          </div>
        </div>

        {/* 轉換結果 */}
        <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
          <h2 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
            <FileText className="w-5 h-5" />
            轉換結果
          </h2>

          <div className="bg-black/20 rounded-lg p-4 min-h-[100px]">
            {isProcessing ? (
              <div className="flex items-center justify-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white"></div>
                <span className="ml-2 text-gray-300">處理中...</span>
              </div>
            ) : (
              <p className="text-gray-300 leading-relaxed">
                {transcription || '請先錄製或上傳音訊檔案，然後點擊「轉換文字」按鈕...'}
              </p>
            )}
          </div>
        </div>

        {/* 隱藏的音訊元素 */}
        {audioUrl && (
          <audio
            ref={audioRef}
            src={audioUrl}
            onEnded={() => setIsPlaying(false)}
            style={{ display: 'none' }}
          />
        )}
      </div>
    </div>
  );
};

export default AMRDeveloperInterface;
