import React, { useState, useEffect, useRef } from 'react';
import { 
  Square, 
  ArrowUp, 
  ArrowDown, 
  ArrowLeft, 
  ArrowRight,
  Battery,
  Camera,
  MapPin,
  Activity,
  Zap,
  AlertTriangle,
  CheckCircle,
  Clock,
  Target,
  Trash2,
  Map,
  Route,
  Flag,
  Home,
  Play,
  Pause,
  Wifi,
  WifiOff,
  Save,
  RotateCw,
  RotateCcw
} from 'lucide-react';

// 從全局變量獲取 ROSLIB (CDN 方式) 或使用 import (npm 方式)
const ROSLIB = window.ROSLIB || (() => {
  // 如果 ROSLIB 不可用，顯示警告
  console.warn('ROSLIB 未加載，請確保已正確安裝 roslibjs 或添加 CDN');
  return {
    Ros: class {
      constructor() {
        this.isConnected = false;
      }
      connect() {
        console.log('模擬 ROS 連接');
        setTimeout(() => {
          this.isConnected = true;
          if (this.onConnection) this.onConnection();
        }, 1000);
      }
      close() {
        this.isConnected = false;
      }
      on() {}
    },
    Topic: class {
      constructor() {}
      subscribe() {}
      unsubscribe() {}
      publish() {}
    },
    Service: class {
      constructor() {}
      callService() {}
    },
    Message: class {
      constructor(data) {
        Object.assign(this, data);
      }
    }
  };
})();

const TurtleBotInterface = () => {
  // ROS 連接狀態
  const [ros, setRos] = useState(null);
  const [rosConnected, setRosConnected] = useState(false);
  const [rosUrl, setRosUrl] = useState('ws://localhost:9090');

  // 機器人狀態
  const [robotStatus, setRobotStatus] = useState('未連接');
  const [batteryLevel, setBatteryLevel] = useState(85);
  const [currentPose, setCurrentPose] = useState({ x: 0, y: 0, theta: 0 });
  const [linearVel, setLinearVel] = useState(0);
  const [angularVel, setAngularVel] = useState(0);
  const [speed, setSpeed] = useState(0.2);
  const [angularSpeed, setAngularSpeed] = useState(0.5);

  // 地圖和導航
  const [currentMode, setCurrentMode] = useState('teleop'); // teleop, mapping, navigation
  const [mapData, setMapData] = useState(null);
  const [waypoints, setWaypoints] = useState([]);
  const [goalPose, setGoalPose] = useState(null);
  const [isNavigating, setIsNavigating] = useState(false);
  const [isMappingActive, setIsMappingActive] = useState(false);

  // UI 狀態
  const [logs, setLogs] = useState([
    { time: new Date().toLocaleTimeString(), type: 'info', message: 'TurtleBot3 界面啟動' }
  ]);

  const mapCanvasRef = useRef(null);
  const cmdVelRef = useRef(null);
  const mapSubRef = useRef(null);
  const odomSubRef = useRef(null);
  const batterySubRef = useRef(null);

  // 初始化 ROS 連接
  useEffect(() => {
    // 檢查 ROSLIB 是否可用
    if (window.ROSLIB) {
      addLog('success', 'ROSLIB 已成功加載');
    } else {
      addLog('warning', 'ROSLIB 未加載，使用模擬模式');
    }

    initROS();
    return () => {
      if (ros && ros.isConnected) {
        ros.close();
      }
    };
  }, []);

  const addLog = (type, message) => {
    const time = new Date().toLocaleTimeString();
    setLogs(prev => [...prev.slice(-9), { time, type, message }]);
  };

  const initROS = () => {
    try {
      const rosInstance = new ROSLIB.Ros({
        url: rosUrl
      });

      rosInstance.on('connection', () => {
        setRosConnected(true);
        setRobotStatus('已連接');
        addLog('success', 'ROS 連接成功');
        setupRosTopics(rosInstance);
      });

      rosInstance.on('error', (error) => {
        setRosConnected(false);
        setRobotStatus('連接錯誤');
        addLog('error', `ROS 連接錯誤: ${error}`);
      });

      rosInstance.on('close', () => {
        setRosConnected(false);
        setRobotStatus('連接斷開');
        addLog('warning', 'ROS 連接斷開');
      });

      if (typeof rosInstance.connect === 'function') {
        rosInstance.connect();
      }

      setRos(rosInstance);
    } catch (error) {
      addLog('error', `初始化失敗: ${error.message}`);
    }
  };

  const setupRosTopics = (rosInstance) => {
    // cmd_vel 發布者 (控制機器人移動)
    cmdVelRef.current = new ROSLIB.Topic({
      ros: rosInstance,
      name: '/cmd_vel',
      messageType: 'geometry_msgs/Twist'
    });

    // odom 訂閱者 (機器人位置)
    odomSubRef.current = new ROSLIB.Topic({
      ros: rosInstance,
      name: '/odom',
      messageType: 'nav_msgs/Odometry'
    });

    odomSubRef.current.subscribe((message) => {
      const { position, orientation } = message.pose.pose;
      const { linear } = message.twist.twist;

      // 轉換四元數到歐拉角
      const theta = 2 * Math.atan2(orientation.z, orientation.w);

      setCurrentPose({
        x: position.x.toFixed(2),
        y: position.y.toFixed(2),
        theta: (theta * 180 / Math.PI).toFixed(1)
      });

      setLinearVel(linear.x.toFixed(2));
    });

    // 地圖訂閱者
    mapSubRef.current = new ROSLIB.Topic({
      ros: rosInstance,
      name: '/map',
      messageType: 'nav_msgs/OccupancyGrid'
    });

    mapSubRef.current.subscribe((message) => {
      setMapData(message);
      drawMap(message);
      addLog('info', '地圖更新');
    });

    // 電池狀態訂閱者
    batterySubRef.current = new ROSLIB.Topic({
      ros: rosInstance,
      name: '/battery_state',
      messageType: 'sensor_msgs/BatteryState'
    });

    batterySubRef.current.subscribe((message) => {
      setBatteryLevel(Math.round(message.percentage * 100));
    });

    addLog('info', 'ROS 話題設置完成');
  };

  // 繪製地圖到 Canvas
  const drawMap = (mapMessage) => {
    const canvas = mapCanvasRef.current;
    if (!canvas || !mapMessage) return;

    const ctx = canvas.getContext('2d');
    const { width, height, data } = mapMessage.info;

    canvas.width = width;
    canvas.height = height;

    const imageData = ctx.createImageData(width, height);

    for (let i = 0; i < data.length; i++) {
      const value = data[i];
      let color;

      if (value === -1) {
        color = [128, 128, 128]; // 未知區域 (灰色)
      } else if (value === 0) {
        color = [255, 255, 255]; // 空閒區域 (白色)
      } else {
        color = [0, 0, 0]; // 障礙物 (黑色)
      }

      const pixelIndex = i * 4;
      imageData.data[pixelIndex] = color[0];     // R
      imageData.data[pixelIndex + 1] = color[1]; // G
      imageData.data[pixelIndex + 2] = color[2]; // B
      imageData.data[pixelIndex + 3] = 255;      // A
    }

    ctx.putImageData(imageData, 0, 0);

    // 繪製機器人位置
    if (currentPose) {
      const robotX = (currentPose.x - mapMessage.info.origin.position.x) / mapMessage.info.resolution;
      const robotY = height - (currentPose.y - mapMessage.info.origin.position.y) / mapMessage.info.resolution;

      ctx.fillStyle = 'blue';
      ctx.beginPath();
      ctx.arc(robotX, robotY, 5, 0, 2 * Math.PI);
      ctx.fill();
    }
  };

  // 發送速度命令
  const publishCmdVel = (linear, angular) => {
    if (!rosConnected || !cmdVelRef.current) return;

    const twist = new ROSLIB.Message({
      linear: { x: linear, y: 0, z: 0 },
      angular: { x: 0, y: 0, z: angular }
    });

    cmdVelRef.current.publish(twist);
  };

  // 停止機器人
  const stopRobot = () => {
    publishCmdVel(0, 0);
    setIsNavigating(false);
    addLog('warning', '機器人停止');
  };

  // 鍵盤控制
  const moveRobot = (direction) => {
    if (!rosConnected) {
      addLog('error', '請先連接 ROS');
      return;
    }

    let linear = 0, angular = 0;

    switch (direction) {
      case 'forward':
        linear = speed;
        break;
      case 'backward':
        linear = -speed;
        break;
      case 'left':
        angular = angularSpeed;
        break;
      case 'right':
        angular = -angularSpeed;
        break;
      case 'rotate_left':
        angular = angularSpeed;
        break;
      case 'rotate_right':
        angular = -angularSpeed;
        break;
      default:
        break;
    }

    publishCmdVel(linear, angular);
    addLog('info', `移動指令: 線速度=${linear}, 角速度=${angular}`);
  };

  // 開始/停止建圖
  const toggleMapping = () => {
    if (!rosConnected) {
      addLog('error', '請先連接 ROS');
      return;
    }

    if (isMappingActive) {
      // 停止建圖並保存地圖
      const saveMapService = new ROSLIB.Service({
        ros: ros,
        name: '/dynamic_map',
        serviceType: 'nav_msgs/GetMap'
      });

      saveMapService.callService(new ROSLIB.ServiceRequest(), (result) => {
        addLog('success', '地圖已保存');
      });

      setIsMappingActive(false);
      setRobotStatus('建圖完成');
    } else {
      setIsMappingActive(true);
      setRobotStatus('建圖中...');
      addLog('info', '開始 SLAM 建圖');
    }
  };

  // 設置導航目標
  const setNavigationGoal = (x, y) => {
    if (!rosConnected) {
      addLog('error', '請先連接 ROS');
      return;
    }

    const goalTopic = new ROSLIB.Topic({
      ros: ros,
      name: '/move_base_simple/goal',
      messageType: 'geometry_msgs/PoseStamped'
    });

    const goal = new ROSLIB.Message({
      header: {
        frame_id: 'map',
        stamp: { sec: 0, nsec: 0 }
      },
      pose: {
        position: { x: x, y: y, z: 0 },
        orientation: { x: 0, y: 0, z: 0, w: 1 }
      }
    });

    goalTopic.publish(goal);
    setGoalPose({ x, y });
    setIsNavigating(true);
    addLog('info', `設置導航目標: (${x.toFixed(2)}, ${y.toFixed(2)})`);
  };

  // 地圖點擊處理
  const handleMapClick = (event) => {
    if (!mapData || currentMode !== 'navigation') return;

    const canvas = mapCanvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // 轉換畫布坐標到世界坐標
    const worldX = x * mapData.info.resolution + mapData.info.origin.position.x;
    const worldY = (canvas.height - y) * mapData.info.resolution + mapData.info.origin.position.y;

    setNavigationGoal(worldX, worldY);
  };

  const getConnectionStatus = () => {
    return rosConnected ? 'text-green-400' : 'text-red-400';
  };

  const getBatteryColor = () => {
    if (batteryLevel > 60) return 'text-green-400';
    if (batteryLevel > 30) return 'text-yellow-400';
    return 'text-red-400';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 p-6">
      <div className="max-w-7xl mx-auto">
        {/* 標題 */}
        <div className="text-center mb-6">
          <h1 className="text-4xl font-bold text-white mb-2">TurtleBot3 控制界面</h1>
          <p className="text-gray-300">ROS Noetic · SLAM · 導航系統</p>
        </div>

        {/* ROS 連接設定 */}
        <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-4 mb-6 border border-white/20">
          <div className="flex items-center gap-4">
            <div className="flex-1">
              <label className="block text-sm font-medium text-gray-300 mb-2">ROS Bridge URL</label>
              <input
                type="text"
                value={rosUrl}
                onChange={(e) => setRosUrl(e.target.value)}
                className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white placeholder-gray-400"
                placeholder="ws://localhost:9090"
              />
            </div>
            <button
              onClick={initROS}
              disabled={rosConnected}
              className="px-6 py-2 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
            >
              {rosConnected ? '已連接' : '連接 ROS'}
            </button>
          </div>
        </div>

        {/* 模式切換 */}
        <div className="flex justify-center gap-2 mb-6">
          <button
            onClick={() => setCurrentMode('teleop')}
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              currentMode === 'teleop' ? 'bg-blue-500 text-white' : 'bg-white/10 text-gray-300'
            }`}
          >
            遙控模式
          </button>
          <button
            onClick={() => setCurrentMode('mapping')}
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              currentMode === 'mapping' ? 'bg-purple-500 text-white' : 'bg-white/10 text-gray-300'
            }`}
          >
            建圖模式
          </button>
          <button
            onClick={() => setCurrentMode('navigation')}
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              currentMode === 'navigation' ? 'bg-green-500 text-white' : 'bg-white/10 text-gray-300'
            }`}
          >
            導航模式
          </button>
        </div>

        {/* 狀態概覽 */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              {rosConnected ? <Wifi className="w-8 h-8 text-green-400" /> : <WifiOff className="w-8 h-8 text-red-400" />}
              <div>
                <p className="text-sm text-gray-300">ROS 狀態</p>
                <p className={`font-semibold ${getConnectionStatus()}`}>{robotStatus}</p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              <Battery className={`w-8 h-8 ${getBatteryColor()}`} />
              <div>
                <p className="text-sm text-gray-300">電量</p>
                <p className={`font-semibold ${getBatteryColor()}`}>{batteryLevel}%</p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              <MapPin className="w-8 h-8 text-purple-400" />
              <div>
                <p className="text-sm text-gray-300">位置</p>
                <p className="font-semibold text-purple-400">
                  ({currentPose.x}, {currentPose.y})
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              <Activity className="w-8 h-8 text-orange-400" />
              <div>
                <p className="text-sm text-gray-300">速度</p>
                <p className="font-semibold text-orange-400">{linearVel} m/s</p>
              </div>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* 地圖顯示 */}
          <div className="lg:col-span-2">
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold text-white flex items-center gap-2">
                  <Map className="w-5 h-5" />
                  {currentMode === 'mapping' ? 'SLAM 地圖' : '導航地圖'}
                </h2>
                <div className="text-sm text-gray-300">
                  {currentMode === 'navigation' && '點擊地圖設置目標點'}
                  {currentMode === 'mapping' && (isMappingActive ? '建圖中...' : '等待開始建圖')}
                </div>
              </div>

              <div className="bg-black/30 rounded-lg p-4 relative">
                <canvas
                  ref={mapCanvasRef}
                  onClick={handleMapClick}
                  className="w-full h-96 bg-gray-800 rounded cursor-crosshair"
                  style={{ maxWidth: '100%', height: '400px' }}
                />
                {!mapData && (
                  <div className="absolute inset-0 flex items-center justify-center">
                    <p className="text-gray-400">等待地圖數據...</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* 控制面板 */}
          <div className="space-y-6">
            {/* 遙控模式 */}
            {currentMode === 'teleop' && (
              <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
                <h2 className="text-xl font-semibold text-white mb-4">鍵盤控制</h2>

                <div className="grid grid-cols-3 gap-2 max-w-48 mx-auto mb-4">
                  <div></div>
                  <button 
                    onMouseDown={() => moveRobot('forward')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowUp className="w-6 h-6 mx-auto" />
                  </button>
                  <div></div>

                  <button 
                    onMouseDown={() => moveRobot('left')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowLeft className="w-6 h-6 mx-auto" />
                  </button>
                  <button 
                    onClick={stopRobot}
                    className="p-3 bg-red-500 hover:bg-red-600 text-white rounded-lg transition-all"
                  >
                    <Square className="w-6 h-6 mx-auto" />
                  </button>
                  <button 
                    onMouseDown={() => moveRobot('right')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowRight className="w-6 h-6 mx-auto" />
                  </button>

                  <div></div>
                  <button 
                    onMouseDown={() => moveRobot('backward')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowDown className="w-6 h-6 mx-auto" />
                  </button>
                  <div></div>
                </div>

                <div className="flex gap-2 mb-4">
                  <button 
                    onMouseDown={() => moveRobot('rotate_left')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="flex-1 flex items-center justify-center gap-2 p-2 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                  >
                    <RotateCcw className="w-4 h-4" />
                    左轉
                  </button>
                  <button 
                    onMouseDown={() => moveRobot('rotate_right')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="flex-1 flex items-center justify-center gap-2 p-2 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                  >
                    <RotateCw className="w-4 h-4" />
                    右轉
                  </button>
                </div>

                <div className="space-y-3">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      線速度: {speed} m/s
                    </label>
                    <input
                      type="range"
                      min="0.1"
                      max="1.0"
                      step="0.1"
                      value={speed}
                      onChange={(e) => setSpeed(parseFloat(e.target.value))}
                      className="w-full h-2 bg-white/20 rounded-lg appearance-none slider"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      角速度: {angularSpeed} rad/s
                    </label>
                    <input
                      type="range"
                      min="0.1"
                      max="2.0"
                      step="0.1"
                      value={angularSpeed}
                      onChange={(e) => setAngularSpeed(parseFloat(e.target.value))}
                      className="w-full h-2 bg-white/20 rounded-lg appearance-none slider"
                    />
                  </div>
                </div>
              </div>
            )}

            {/* 建圖模式 */}
            {currentMode === 'mapping' && (
              <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
                <h2 className="text-xl font-semibold text-white mb-4">SLAM 建圖</h2>

                <div className="space-y-4">
                  <button
                    onClick={toggleMapping}
                    disabled={!rosConnected}
                    className={`w-full flex items-center justify-center gap-2 px-4 py-3 ${
                      isMappingActive ? 'bg-red-500 hover:bg-red-600' : 'bg-purple-500 hover:bg-purple-600'
                    } disabled:bg-gray-600 text-white rounded-lg font-medium transition-all`}
                  >
                    <Map className="w-5 h-5" />
                    {isMappingActive ? '停止建圖' : '開始建圖'}
                  </button>

                  <button
                    disabled={!rosConnected || !mapData}
                    className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg font-medium transition-all"
                  >
                    <Save className="w-5 h-5" />
                    保存地圖
                  </button>

                  <p className="text-sm text-gray-300">
                    使用遙控功能移動機器人來建立環境地圖
                  </p>
                </div>
              </div>
            )}

            {/* 導航模式 */}
            {currentMode === 'navigation' && (
              <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
                <h2 className="text-xl font-semibold text-white mb-4">自主導航</h2>

                <div className="space-y-4">
                  {goalPose && (
                    <div className="bg-black/20 rounded-lg p-3">
                      <p className="text-sm text-gray-300">目標位置:</p>
                      <p className="text-white">({goalPose.x.toFixed(2)}, {goalPose.y.toFixed(2)})</p>
                    </div>
                  )}

                  <button
                    onClick={stopRobot}
                    disabled={!rosConnected}
                    className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-red-500 hover:bg-red-600 disabled:bg-gray-600 text-white rounded-lg font-medium transition-all"
                  >
                    <Square className="w-5 h-5" />
                    停止導航
                  </button>

                  <p className="text-sm text-gray-300">
                    點擊地圖設置導航目標點
                  </p>
                </div>
              </div>
            )}

            {/* 系統日誌 */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h2 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                <Clock className="w-5 h-5" />
                系統日誌
              </h2>

              <div className="space-y-2 max-h-48 overflow-y-auto">
                {logs.map((log, index) => (
                  <div key={index} className="flex items-start gap-3 text-sm">
                    <span className="text-gray-400 font-mono text-xs">{log.time}</span>
                    {log.type === 'success' && <CheckCircle className="w-4 h-4 text-green-400 mt-0.5" />}
                    {log.type === 'warning' && <AlertTriangle className="w-4 h-4 text-yellow-400 mt-0.5" />}
                    {log.type === 'info' && <Zap className="w-4 h-4 text-blue-400 mt-0.5" />}
                    {log.type === 'error' && <AlertTriangle className="w-4 h-4 text-red-400 mt-0.5" />}
                    <span className="text-gray-300 text-xs">{log.message}</span>
                  </div>
                ))}
              </div>
            </div>

            {/* 機器人資訊 */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h2 className="text-xl font-semibold text-white mb-4">機器人資訊</h2>

              <div className="space-y-3 text-sm">
                <div className="flex justify-between">
                  <span className="text-gray-300">位置 X:</span>
                  <span className="text-white">{currentPose.x} m</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-300">位置 Y:</span>
                  <span className="text-white">{currentPose.y} m</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-300">方向角:</span>
                  <span className="text-white">{currentPose.theta}°</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-300">線速度:</span>
                  <span className="text-white">{linearVel} m/s</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-300">角速度:</span>
                  <span className="text-white">{angularVel} rad/s</span>
                </div>
              </div>
            </div>

            {/* 快速動作 */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h2 className="text-xl font-semibold text-white mb-4">快速動作</h2>

              <div className="grid grid-cols-1 gap-2">
                <button 
                  onClick={() => setNavigationGoal(0, 0)}
                  disabled={!rosConnected || currentMode !== 'navigation'}
                  className="flex items-center justify-center gap-2 px-3 py-2 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                >
                  <Home className="w-4 h-4" />
                  回到原點
                </button>
                <button 
                  onClick={stopRobot}
                  disabled={!rosConnected}
                  className="flex items-center justify-center gap-2 px-3 py-2 bg-red-500 hover:bg-red-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                >
                  <Square className="w-4 h-4" />
                  緊急停止
                </button>
                <button 
                  onClick={() => window.open('http://localhost:8080', '_blank')}
                  className="flex items-center justify-center gap-2 px-3 py-2 bg-orange-500 hover:bg-orange-600 text-white rounded-lg text-sm transition-all"
                >
                  <Camera className="w-4 h-4" />
                  攝影機視圖
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TurtleBotInterface;
