import React, { useState, useEffect, useRef, useCallback } from 'react';
import { 
  Square, 
  ArrowUp, 
  ArrowDown, 
  ArrowLeft, 
  ArrowRight,
  Battery,
  Wifi,
  WifiOff,
  RotateCw,
  RotateCcw,
  Plus,
  Minus,
  Home
} from 'lucide-react';

// 從全局變量獲取 ROSLIB (CDN 方式)
const ROSLIB = window.ROSLIB || (() => {
  console.warn('ROSLIB 未加載，使用模擬模式');
  return {
    Ros: class {
      constructor() {
        this.isConnected = false;
      }
      connect() {
        console.log('模擬 ROS 連接');
        setTimeout(() => {
          this.isConnected = true;
          if (this.onConnection) this.onConnection();
        }, 1000);
      }
      close() {
        this.isConnected = false;
      }
      on() {}
    },
    Topic: class {
      publish() {}
      subscribe() {}
    },
    Service: class {
      callService() {}
    },
    Message: class {
      constructor(data) {
        Object.assign(this, data);
      }
    }
  };
})();

const TurtleBotInterface = () => {
  // ROS 連接狀態
  const [rosConnected, setRosConnected] = useState(false);
  const [rosUrl, setRosUrl] = useState('ws://localhost:9090');

  // 機器人狀態
  const [robotStatus, setRobotStatus] = useState('未連接');
  const [batteryLevel, setBatteryLevel] = useState(85);
  const [currentPose, setCurrentPose] = useState({ x: 0, y: 0, theta: 0 });
  const [speed, setSpeed] = useState(0.2);
  const [angularSpeed, setAngularSpeed] = useState(0.5);

  // 地圖和導航
  const [mapData, setMapData] = useState(null);

  // 地圖縮放和拖拽狀態
  const [mapZoom, setMapZoom] = useState(1.0);
  const [mapOffset, setMapOffset] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [lastMousePos, setLastMousePos] = useState({ x: 0, y: 0 });

  // UI 狀態
  const mapCanvasRef = useRef(null);
  const cmdVelRef = useRef(null);
  const mapSubRef = useRef(null);
  const odomSubRef = useRef(null);
  const rosRef = useRef(null);

  const addLog = useCallback((type, message) => {
    console.log(`[${new Date().toLocaleTimeString()}] ${type}: ${message}`);
  }, []);

  // 繪製地圖到 Canvas（移動到上面了）

  // 重繪地圖的函數
  const redrawMap = useCallback((mapMessage) => {
    const canvas = mapCanvasRef.current;
    if (!canvas || !mapMessage) return;

    const ctx = canvas.getContext('2d');
    const { width, height, resolution, origin } = mapMessage.info;
    const { data } = mapMessage;

    if (!data || data.length === 0) return;

    const displayWidth = canvas.clientWidth || 800;
    const displayHeight = canvas.clientHeight || 600;
    canvas.width = displayWidth;
    canvas.height = displayHeight;

    // 清空畫布 - 使用淺灰色背景
    ctx.fillStyle = '#e5e7eb';
    ctx.fillRect(0, 0, displayWidth, displayHeight);

    const baseScaleX = displayWidth / width;
    const baseScaleY = displayHeight / height;
    const baseScale = Math.min(baseScaleX, baseScaleY) * 0.8;
    const finalScale = baseScale * mapZoom;

    const centerX = displayWidth / 2;
    const centerY = displayHeight / 2;
    const offsetX = centerX - (width * finalScale) / 2 + mapOffset.x;
    const offsetY = centerY - (height * finalScale) / 2 + mapOffset.y;

    // 繪製地圖
    for (let y = 0; y < height; y++) {
      for (let x = 0; x < width; x++) {
        const dataIndex = y * width + x;
        const value = data[dataIndex];

        let color;
        if (value === -1) {
          continue; // 未知區域不繪製
        } else if (value >= 0 && value < 25) {
          color = '#ffffff'; // 空閒區域 (白色)
        } else if (value >= 65) {
          color = '#000000'; // 障礙物 (黑色)
        } else {
          color = '#d1d5db'; // 部分已知區域
        }

        const pixelX = offsetX + x * finalScale;
        const pixelY = offsetY + y * finalScale;

        if (pixelX >= 0 && pixelX < displayWidth && pixelY >= 0 && pixelY < displayHeight) {
          ctx.fillStyle = color;
          ctx.fillRect(pixelX, pixelY, Math.ceil(finalScale), Math.ceil(finalScale));
        }
      }
    }

    // 繪製機器人位置
    if (currentPose && currentPose.x !== undefined) {
      const robotWorldX = parseFloat(currentPose.x);
      const robotWorldY = parseFloat(currentPose.y);

      const robotMapX = (robotWorldX - origin.position.x) / resolution;
      const robotMapY = (origin.position.y + height * resolution - robotWorldY) / resolution;

      const robotDisplayX = offsetX + robotMapX * finalScale;
      const robotDisplayY = offsetY + robotMapY * finalScale;

      if (robotDisplayX >= -50 && robotDisplayX <= displayWidth + 50 && 
          robotDisplayY >= -50 && robotDisplayY <= displayHeight + 50) {

        // 繪製機器人（紅色三角形，像圖片中一樣）
        const theta = parseFloat(currentPose.theta) * Math.PI / 180;
        const size = 15 * mapZoom;

        ctx.fillStyle = '#ef4444';
        ctx.strokeStyle = '#dc2626';
        ctx.lineWidth = 2;

        ctx.beginPath();
        ctx.moveTo(
          robotDisplayX + Math.cos(theta) * size,
          robotDisplayY - Math.sin(theta) * size
        );
        ctx.lineTo(
          robotDisplayX + Math.cos(theta + 2.3) * size * 0.7,
          robotDisplayY - Math.sin(theta + 2.3) * size * 0.7
        );
        ctx.lineTo(
          robotDisplayX + Math.cos(theta - 2.3) * size * 0.7,
          robotDisplayY - Math.sin(theta - 2.3) * size * 0.7
        );
        ctx.closePath();
        ctx.fill();
        ctx.stroke();

        // 繪製機器人軌跡（紅色虛線）
        ctx.strokeStyle = '#ef4444';
        ctx.lineWidth = 1;
        ctx.setLineDash([3, 3]);
        ctx.beginPath();
        ctx.arc(robotDisplayX, robotDisplayY, 30 * mapZoom, 0, 2 * Math.PI);
        ctx.stroke();
        ctx.setLineDash([]);
      }
    }
  }, [currentPose, mapZoom, mapOffset]);

  const setupRosTopics = useCallback((rosInstance) => {
    cmdVelRef.current = new ROSLIB.Topic({
      ros: rosInstance,
      name: '/cmd_vel',
      messageType: 'geometry_msgs/Twist'
    });

    odomSubRef.current = new ROSLIB.Topic({
      ros: rosInstance,
      name: '/odom',
      messageType: 'nav_msgs/Odometry'
    });

    odomSubRef.current.subscribe((message) => {
      const { position, orientation } = message.pose.pose;

      const theta = 2 * Math.atan2(orientation.z, orientation.w);

      setCurrentPose({
        x: parseFloat(position.x.toFixed(2)),
        y: parseFloat(position.y.toFixed(2)),
        theta: (theta * 180 / Math.PI).toFixed(1)
      });
    });

    mapSubRef.current = new ROSLIB.Topic({
      ros: rosInstance,
      name: '/map',
      messageType: 'nav_msgs/OccupancyGrid'
    });

    addLog('info', '正在訂閱地圖話題 /map...');

    mapSubRef.current.subscribe((message) => {
      console.log('🗺️ 收到地圖數據!', {
        width: message.info.width,
        height: message.info.height,
        resolution: message.info.resolution,
        dataLength: message.data ? message.data.length : 0
      });

      if (message.data && message.data.length > 0) {
        setMapData(message);
        // 延遲繪製，確保 redrawMap 函數已定義
        setTimeout(() => {
          if (mapCanvasRef.current) {
            redrawMap(message);
          }
        }, 100);
        addLog('success', `地圖更新: ${message.info.width}x${message.info.height}`);
      }
    });

    const batterySimulation = setInterval(() => {
      setBatteryLevel(prev => Math.max(20, prev - 0.1));
    }, 10000);

    rosInstance.batterySimulation = batterySimulation;
    addLog('success', 'ROS 話題設置完成');
  }, [addLog, redrawMap]);

  // 繪製地圖到 Canvas
  // 移除未使用的 drawMap 變數

  // 初始化 ROS 連接
  useEffect(() => {
    if (window.ROSLIB) {
      addLog('success', 'ROSLIB 已成功加載');
    } else {
      addLog('warning', 'ROSLIB 未加載，使用模擬模式');
    }

    let reconnectTimer = null;

    const initAndSetupROS = () => {
      try {
        // 清除之前的定時器
        if (reconnectTimer) {
          clearTimeout(reconnectTimer);
        }

        const rosInstance = new ROSLIB.Ros({ 
          url: rosUrl,
          groovyCompatibility: false
        });

        rosInstance.on('connection', () => {
          setRosConnected(true);
          setRobotStatus('已連接');
          addLog('success', 'ROS 連接成功');
          setupRosTopics(rosInstance);
        });

        rosInstance.on('error', (error) => {
          console.error('ROS 連接錯誤:', error);
          setRosConnected(false);
          setRobotStatus('連接錯誤');
          addLog('error', `ROS 連接錯誤: ${error}`);

          // 3秒後自動重連
          reconnectTimer = setTimeout(() => {
            addLog('info', '嘗試重新連接...');
            initAndSetupROS();
          }, 3000);
        });

        rosInstance.on('close', () => {
          setRosConnected(false);
          setRobotStatus('連接斷開');
          addLog('warning', 'ROS 連接斷開');

          // 2秒後自動重連
          reconnectTimer = setTimeout(() => {
            addLog('info', '嘗試重新連接...');
            initAndSetupROS();
          }, 2000);
        });

        // 手動連接
        try {
          rosInstance.connect(rosUrl);
        } catch (connectError) {
          console.error('連接失敗:', connectError);
          addLog('error', '手動連接失敗');
        }

        rosRef.current = rosInstance;
      } catch (error) {
        addLog('error', `初始化失敗: ${error.message}`);

        // 5秒後重試
        reconnectTimer = setTimeout(() => {
          addLog('info', '重試初始化...');
          initAndSetupROS();
        }, 5000);
      }
    };

    initAndSetupROS();

    // 清理函數
    return () => {
      if (reconnectTimer) {
        clearTimeout(reconnectTimer);
      }
      if (rosRef.current) {
        try {
          rosRef.current.close();
        } catch (e) {
          console.log('關閉連接時發生錯誤:', e);
        }
      }
    };
  }, [rosUrl, addLog, setupRosTopics]);

  // 發送速度命令
  const publishCmdVel = useCallback((linear, angular) => {
    if (!rosConnected || !cmdVelRef.current) return;

    const twist = new ROSLIB.Message({
      linear: { x: linear, y: 0, z: 0 },
      angular: { x: 0, y: 0, z: angular }
    });

    cmdVelRef.current.publish(twist);
  }, [rosConnected]);

  // 停止機器人
  const stopRobot = useCallback(() => {
    publishCmdVel(0, 0);
    addLog('warning', '機器人停止');
  }, [publishCmdVel, addLog]);

  // 移動機器人
  const moveRobot = useCallback((direction) => {
    if (!rosConnected) {
      addLog('error', '請先連接 ROS');
      return;
    }

    let linear = 0, angular = 0;

    switch (direction) {
      case 'forward':
        linear = speed;
        break;
      case 'backward':
        linear = -speed;
        break;
      case 'left':
        angular = angularSpeed;
        break;
      case 'right':
        angular = -angularSpeed;
        break;
      case 'rotate_left':
        angular = angularSpeed;
        break;
      case 'rotate_right':
        angular = -angularSpeed;
        break;
      default:
        break;
    }

    publishCmdVel(linear, angular);
    addLog('info', `移動指令: 線速度=${linear.toFixed(2)}, 角速度=${angular.toFixed(2)}`);
  }, [rosConnected, speed, angularSpeed, publishCmdVel, addLog]);

  // 地圖縮放控制
  const handleZoomIn = () => {
    const newZoom = Math.min(5.0, mapZoom * 1.2);
    setMapZoom(newZoom);
    if (mapData) setTimeout(() => redrawMap(mapData), 10);
  };

  const handleZoomOut = () => {
    const newZoom = Math.max(0.5, mapZoom * 0.8);
    setMapZoom(newZoom);
    if (mapData) setTimeout(() => redrawMap(mapData), 10);
  };

  // 重置地圖視圖
  const resetMapView = () => {
    setMapZoom(1.0);
    setMapOffset({ x: 0, y: 0 });
    if (mapData) setTimeout(() => redrawMap(mapData), 10);
  };

  // 滑鼠事件處理
  const handleMapMouseDown = (event) => {
    event.preventDefault();
    event.stopPropagation();
    setIsDragging(true);
    setLastMousePos({ x: event.clientX, y: event.clientY });
  };

  const handleMapMouseMove = (event) => {
    if (!isDragging) return;

    const deltaX = event.clientX - lastMousePos.x;
    const deltaY = event.clientY - lastMousePos.y;

    setMapOffset(prev => ({
      x: prev.x + deltaX,
      y: prev.y + deltaY
    }));

    setLastMousePos({ x: event.clientX, y: event.clientY });

    if (mapData) redrawMap(mapData);
  };

  const handleMapMouseUp = () => {
    setIsDragging(false);
  };

  // 滾輪縮放（防止頁面滾動）
  const handleMapWheel = (event) => {
    event.preventDefault();
    event.stopPropagation();

    const delta = event.deltaY > 0 ? 0.9 : 1.1;
    const newZoom = Math.max(0.5, Math.min(5.0, mapZoom * delta));
    setMapZoom(newZoom);

    if (mapData) setTimeout(() => redrawMap(mapData), 10);
  };

  const initROS = () => {
    // 強制重新加載頁面來重置所有連接
    addLog('info', '重新初始化 ROS 連接...');
    setTimeout(() => {
      window.location.reload();
    }, 500);
  };

  const getBatteryColor = () => {
    if (batteryLevel > 60) return 'text-green-400';
    if (batteryLevel > 30) return 'text-yellow-400';
    return 'text-red-400';
  };

  return (
    <div className="min-h-screen bg-gray-100 relative overflow-hidden">
      {/* 狀態欄 */}
      <div className="bg-blue-500 text-white px-4 py-2 flex justify-between items-center">
        <div className="flex items-center gap-4">
          <span className="font-semibold">TurtleBot3 控制界面</span>
          <div className="flex items-center gap-2">
            {rosConnected ? <Wifi className="w-4 h-4" /> : <WifiOff className="w-4 h-4" />}
            <span className="text-sm">{robotStatus}</span>
          </div>
        </div>
        <div className="flex items-center gap-4">
          <div className="flex items-center gap-2">
            <Battery className={`w-4 h-4 ${getBatteryColor()}`} />
            <span className="text-sm">{batteryLevel.toFixed(1)}%</span>
          </div>
          <div className="text-sm">位置: ({currentPose.x}, {currentPose.y})</div>
        </div>
      </div>

      {/* 主要地圖區域 */}
      <div className="relative flex-1" style={{ height: 'calc(100vh - 160px)' }}>
        <canvas
          ref={mapCanvasRef}
          onMouseDown={handleMapMouseDown}
          onMouseMove={handleMapMouseMove}
          onMouseUp={handleMapMouseUp}
          onMouseLeave={handleMapMouseUp}
          onWheel={handleMapWheel}
          className="w-full h-full cursor-move select-none bg-gray-200"
          style={{ touchAction: 'none' }}
        />

        {/* 地圖控制按鈕 */}
        <div className="absolute top-4 right-4 flex flex-col gap-2">
          <button
            onClick={handleZoomIn}
            className="w-10 h-10 bg-white border border-gray-300 rounded shadow hover:bg-gray-50 flex items-center justify-center"
            title="放大"
          >
            <Plus className="w-5 h-5" />
          </button>
          <button
            onClick={handleZoomOut}
            className="w-10 h-10 bg-white border border-gray-300 rounded shadow hover:bg-gray-50 flex items-center justify-center"
            title="縮小"
          >
            <Minus className="w-5 h-5" />
          </button>
          <button
            onClick={resetMapView}
            className="w-10 h-10 bg-white border border-gray-300 rounded shadow hover:bg-gray-50 flex items-center justify-center"
            title="重置視圖"
          >
            <Home className="w-5 h-5" />
          </button>
        </div>

        {/* 縮放顯示 */}
        <div className="absolute top-4 left-4 bg-black/60 text-white px-3 py-2 rounded text-sm">
          縮放: {(mapZoom * 100).toFixed(0)}%
        </div>

        {/* 緊急停止按鈕 */}
        <div className="absolute bottom-4 left-4">
          <button
            onClick={stopRobot}
            className="bg-red-500 hover:bg-red-600 text-white px-6 py-3 rounded-lg font-bold text-lg shadow-lg"
          >
            🛑 緊急停止
          </button>
        </div>
      </div>

      {/* 底部控制面板 */}
      <div className="bg-white border-t border-gray-300 p-4">
        <div className="max-w-6xl mx-auto flex justify-between items-center">
          {/* 連接控制 */}
          <div className="flex items-center gap-4">
            <input
              type="text"
              value={rosUrl}
              onChange={(e) => setRosUrl(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded text-sm"
              placeholder="ws://localhost:9090"
            />
            <button
              onClick={initROS}
              disabled={rosConnected}
              className="px-4 py-2 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white rounded text-sm"
            >
              {rosConnected ? '已連接' : '連接'}
            </button>
          </div>

          {/* 速度控制 */}
          <div className="flex items-center gap-6">
            <div className="text-center">
              <div className="text-sm text-gray-600 mb-1">機器人速度</div>
              <div className="flex items-center gap-2">
                <button
                  onClick={() => setSpeed(Math.max(0.1, speed - 0.1))}
                  className="w-8 h-8 bg-gray-200 hover:bg-gray-300 rounded flex items-center justify-center"
                >
                  <Minus className="w-4 h-4" />
                </button>
                <span className="text-sm font-mono w-16 text-center">{speed.toFixed(1)} m/s</span>
                <button
                  onClick={() => setSpeed(Math.min(1.0, speed + 0.1))}
                  className="w-8 h-8 bg-gray-200 hover:bg-gray-300 rounded flex items-center justify-center"
                >
                  <Plus className="w-4 h-4" />
                </button>
              </div>
            </div>

            <div className="text-center">
              <div className="text-sm text-gray-600 mb-1">角速度</div>
              <div className="flex items-center gap-2">
                <button
                  onClick={() => setAngularSpeed(Math.max(0.1, angularSpeed - 0.1))}
                  className="w-8 h-8 bg-gray-200 hover:bg-gray-300 rounded flex items-center justify-center"
                >
                  <Minus className="w-4 h-4" />
                </button>
                <span className="text-sm font-mono w-16 text-center">{angularSpeed.toFixed(1)} rad/s</span>
                <button
                  onClick={() => setAngularSpeed(Math.min(2.0, angularSpeed + 0.1))}
                  className="w-8 h-8 bg-gray-200 hover:bg-gray-300 rounded flex items-center justify-center"
                >
                  <Plus className="w-4 h-4" />
                </button>
              </div>
            </div>
          </div>

          {/* 方向控制 */}
          <div className="flex items-center gap-4">
            <div className="grid grid-cols-3 gap-1">
              <div></div>
              <button
                onMouseDown={() => moveRobot('forward')}
                onMouseUp={stopRobot}
                disabled={!rosConnected}
                className="w-12 h-12 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white rounded flex items-center justify-center"
              >
                <ArrowUp className="w-6 h-6" />
              </button>
              <div></div>

              <button
                onMouseDown={() => moveRobot('left')}
                onMouseUp={stopRobot}
                disabled={!rosConnected}
                className="w-12 h-12 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white rounded flex items-center justify-center"
              >
                <ArrowLeft className="w-6 h-6" />
              </button>
              <button
                onClick={stopRobot}
                className="w-12 h-12 bg-red-500 hover:bg-red-600 text-white rounded flex items-center justify-center"
              >
                <Square className="w-6 h-6" />
              </button>
              <button
                onMouseDown={() => moveRobot('right')}
                onMouseUp={stopRobot}
                disabled={!rosConnected}
                className="w-12 h-12 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white rounded flex items-center justify-center"
              >
                <ArrowRight className="w-6 h-6" />
              </button>

              <div></div>
              <button
                onMouseDown={() => moveRobot('backward')}
                onMouseUp={stopRobot}
                disabled={!rosConnected}
                className="w-12 h-12 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white rounded flex items-center justify-center"
              >
                <ArrowDown className="w-6 h-6" />
              </button>
              <div></div>
            </div>

            <div className="flex flex-col gap-1">
              <button
                onMouseDown={() => moveRobot('rotate_left')}
                onMouseUp={stopRobot}
                disabled={!rosConnected}
                className="w-12 h-6 bg-green-500 hover:bg-green-600 disabled:bg-gray-400 text-white rounded flex items-center justify-center text-xs"
              >
                <RotateCcw className="w-4 h-4" />
              </button>
              <button
                onMouseDown={() => moveRobot('rotate_right')}
                onMouseUp={stopRobot}
                disabled={!rosConnected}
                className="w-12 h-6 bg-green-500 hover:bg-green-600 disabled:bg-gray-400 text-white rounded flex items-center justify-center text-xs"
              >
                <RotateCw className="w-4 h-4" />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* 載入提示 */}
      {!mapData && (
        <div className="absolute inset-0 flex items-center justify-center bg-black/20">
          <div className="bg-white p-6 rounded-lg shadow-lg text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto mb-4"></div>
            <p className="text-gray-600">等待地圖數據...</p>
            <p className="text-sm text-gray-400 mt-2">請確保 SLAM 節點正在運行</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default TurtleBotInterface;
