import React, { useState, useEffect, useRef, useCallback } from 'react';
import { 
  Square, 
  ArrowUp, 
  ArrowDown, 
  ArrowLeft, 
  ArrowRight,
  Battery,
  Camera,
  MapPin,
  Activity,
  Zap,
  AlertTriangle,
  CheckCircle,
  Clock,
  Target,
  Trash2,
  Map,
  Route,
  Flag,
  Home,
  Play,
  Pause
} from 'lucide-react';

const AMRDeveloperInterface = () => {
  const [robotStatus, setRobotStatus] = useState('待機中');
  const [batteryLevel, setBatteryLevel] = useState(85);
  const [currentPosition, setCurrentPosition] = useState({ x: 125, y: 100 });
  const [targetPosition, setTargetPosition] = useState({ x: 200, y: 150 });
  const [speed, setSpeed] = useState(50);
  const [isMoving, setIsMoving] = useState(false);
  const [isMappingMode, setIsMappingMode] = useState(false);
  const [isPatrolling, setIsPatrolling] = useState(false);
  const [currentMode, setCurrentMode] = useState('control'); // control, mapping, waypoints

  // 地圖和導航相關
  const [stations, setStations] = useState([
    { id: 1, name: '充電站', x: 50, y: 50, type: 'charging', color: 'bg-green-500' },
    { id: 2, name: '工作站A', x: 300, y: 80, type: 'work', color: 'bg-blue-500' },
    { id: 3, name: '工作站B', x: 350, y: 200, type: 'work', color: 'bg-blue-500' }
  ]);
  const [waypoints, setWaypoints] = useState([
    { id: 1, x: 100, y: 75, order: 1 },
    { id: 2, x: 250, y: 120, order: 2 },
    { id: 3, x: 320, y: 180, order: 3 }
  ]);
  const [obstacles] = useState([
    { id: 1, x: 180, y: 140, width: 40, height: 30 },
    { id: 2, x: 280, y: 60, width: 30, height: 40 }
  ]);

  const [newStationName, setNewStationName] = useState('');
  const [currentWaypointIndex, setCurrentWaypointIndex] = useState(0);

  const mapRef = useRef(null);

  const [logs, setLogs] = useState([
    { time: '14:30:25', type: 'info', message: '機器人初始化完成' },
    { time: '14:30:26', type: 'success', message: '導航系統就緒' },
    { time: '14:30:27', type: 'info', message: '等待指令中...' }
  ]);

  // 模擬電池消耗
  useEffect(() => {
    const interval = setInterval(() => {
      setBatteryLevel(prev => Math.max(0, prev - 0.1));
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  // 模擬機器人移動
  useEffect(() => {
    if (isMoving) {
      const interval = setInterval(() => {
        setCurrentPosition(prev => {
          const dx = targetPosition.x - prev.x;
          const dy = targetPosition.y - prev.y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < 5) {
            setIsMoving(false);
            setRobotStatus('已到達目標位置');
            addLog('success', '已到達目標位置');

            // 如果在巡邏模式，移動到下一個航點
            if (isPatrolling && waypoints.length > 0) {
              setTimeout(() => {
                const nextIndex = (currentWaypointIndex + 1) % waypoints.length;
                setCurrentWaypointIndex(nextIndex);
                const nextWaypoint = waypoints[nextIndex];
                setTargetPosition({ x: nextWaypoint.x, y: nextWaypoint.y });
                setIsMoving(true);
                setRobotStatus('巡邏中...');
                addLog('info', `移動至航點 ${nextWaypoint.order}`);
              }, 1000);
            }

            return targetPosition;
          }

          const moveSpeed = speed / 100 * 2;
          const newX = prev.x + (dx / distance) * moveSpeed;
          const newY = prev.y + (dy / distance) * moveSpeed;

          return { x: newX, y: newY };
        });
      }, 100);

      return () => clearInterval(interval);
    }
  }, [isMoving, targetPosition, speed, isPatrolling, currentWaypointIndex, waypoints]);

  const addLog = (type, message) => {
    const now = new Date();
    const time = now.toLocaleTimeString();
    setLogs(prev => [...prev.slice(-4), { time, type, message }]);
  };

  const moveRobot = (direction) => {
    if (isMoving) return;

    const moveDistance = 50;
    let newTarget = { ...currentPosition };

    switch (direction) {
      case 'up':
        newTarget.y -= moveDistance;
        break;
      case 'down':
        newTarget.y += moveDistance;
        break;
      case 'left':
        newTarget.x -= moveDistance;
        break;
      case 'right':
        newTarget.x += moveDistance;
        break;
      default:
        return;
    }

    newTarget.x = Math.max(10, Math.min(390, newTarget.x));
    newTarget.y = Math.max(10, Math.min(240, newTarget.y));

    setTargetPosition(newTarget);
    setIsMoving(true);
    setRobotStatus('移動中...');
    addLog('info', `開始移動至 (${Math.round(newTarget.x)}, ${Math.round(newTarget.y)})`);
  };

  const stopRobot = () => {
    setIsMoving(false);
    setIsPatrolling(false);
    setRobotStatus('已停止');
    addLog('warning', '緊急停止指令執行');
  };

  const goToPosition = () => {
    if (isMoving) return;
    setIsMoving(true);
    setRobotStatus('導航中...');
    addLog('info', `導航至目標位置 (${Math.round(targetPosition.x)}, ${Math.round(targetPosition.y)})`);
  };

  const handleMapClick = (event) => {
    if (!mapRef.current) return;

    const rect = mapRef.current.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    if (currentMode === 'mapping') {
      // 添加新站點
      if (newStationName.trim()) {
        const newStation = {
          id: Date.now(),
          name: newStationName,
          x: x,
          y: y,
          type: 'work',
          color: 'bg-blue-500'
        };
        setStations(prev => [...prev, newStation]);
        setNewStationName('');
        addLog('success', `新增站點: ${newStation.name}`);
      }
    } else if (currentMode === 'waypoints') {
      // 添加新航點
      const newWaypoint = {
        id: Date.now(),
        x: x,
        y: y,
        order: waypoints.length + 1
      };
      setWaypoints(prev => [...prev, newWaypoint]);
      addLog('success', `新增航點 ${newWaypoint.order}`);
    } else {
      // 設定目標位置
      setTargetPosition({ x, y });
    }
  };

  const deleteStation = (stationId) => {
    setStations(prev => prev.filter(s => s.id !== stationId));
    addLog('warning', '刪除站點');
  };

  const deleteWaypoint = (waypointId) => {
    setWaypoints(prev => {
      const filtered = prev.filter(w => w.id !== waypointId);
      return filtered.map((w, index) => ({ ...w, order: index + 1 }));
    });
    addLog('warning', '刪除航點');
  };

  const startPatrol = () => {
    if (waypoints.length === 0) {
      addLog('error', '沒有設定航點，無法開始巡邏');
      return;
    }

    setIsPatrolling(true);
    setCurrentWaypointIndex(0);
    setTargetPosition({ x: waypoints[0].x, y: waypoints[0].y });
    setIsMoving(true);
    setRobotStatus('巡邏中...');
    addLog('info', '開始自動巡邏');
  };

  const goToStation = (station) => {
    if (isMoving) return;
    setTargetPosition({ x: station.x, y: station.y });
    setIsMoving(true);
    setRobotStatus(`前往 ${station.name}`);
    addLog('info', `導航至 ${station.name}`);
  };

  const startMapping = () => {
    setIsMappingMode(!isMappingMode);
    setCurrentMode(isMappingMode ? 'control' : 'mapping');
    setRobotStatus(isMappingMode ? '待機中' : '建圖模式');
    addLog('info', isMappingMode ? '結束建圖模式' : '開始建圖模式');
  };

  const getBatteryColor = () => {
    if (batteryLevel > 60) return 'text-green-400';
    if (batteryLevel > 30) return 'text-yellow-400';
    return 'text-red-400';
  };

  const getStatusColor = () => {
    if (robotStatus.includes('移動') || robotStatus.includes('導航') || robotStatus.includes('巡邏')) return 'text-blue-400';
    if (robotStatus.includes('已到達')) return 'text-green-400';
    if (robotStatus.includes('停止')) return 'text-red-400';
    if (robotStatus.includes('建圖')) return 'text-purple-400';
    return 'text-gray-400';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 p-6">
      <div className="max-w-7xl mx-auto">
        {/* 標題 */}
        <div className="text-center mb-6">
          <h1 className="text-4xl font-bold text-white mb-2">AMR 智能導航系統</h1>
          <p className="text-gray-300">建圖 · 導航 · 巡邏控制台</p>
        </div>

        {/* 模式切換 */}
        <div className="flex justify-center gap-2 mb-6">
          <button
            onClick={() => setCurrentMode('control')}
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              currentMode === 'control' ? 'bg-blue-500 text-white' : 'bg-white/10 text-gray-300'
            }`}
          >
            控制模式
          </button>
          <button
            onClick={() => setCurrentMode('mapping')}
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              currentMode === 'mapping' ? 'bg-purple-500 text-white' : 'bg-white/10 text-gray-300'
            }`}
          >
            建圖模式
          </button>
          <button
            onClick={() => setCurrentMode('waypoints')}
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              currentMode === 'waypoints' ? 'bg-green-500 text-white' : 'bg-white/10 text-gray-300'
            }`}
          >
            航點模式
          </button>
        </div>

        {/* 狀態概覽 */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              <Activity className="w-8 h-8 text-blue-400" />
              <div>
                <p className="text-sm text-gray-300">狀態</p>
                <p className={`font-semibold ${getStatusColor()}`}>{robotStatus}</p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              <Battery className={`w-8 h-8 ${getBatteryColor()}`} />
              <div>
                <p className="text-sm text-gray-300">電量</p>
                <p className={`font-semibold ${getBatteryColor()}`}>{batteryLevel.toFixed(1)}%</p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              <MapPin className="w-8 h-8 text-purple-400" />
              <div>
                <p className="text-sm text-gray-300">位置</p>
                <p className="font-semibold text-purple-400">
                  ({Math.round(currentPosition.x)}, {Math.round(currentPosition.y)})
                </p>
              </div>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* 地圖區域 */}
          <div className="lg:col-span-2">
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold text-white flex items-center gap-2">
                  <Map className="w-5 h-5" />
                  智能地圖
                </h2>
                <div className="text-sm text-gray-300">
                  {currentMode === 'mapping' && '點擊地圖新增站點'}
                  {currentMode === 'waypoints' && '點擊地圖新增航點'}
                  {currentMode === 'control' && '點擊設定目標位置'}
                </div>
              </div>

              <div 
                ref={mapRef}
                className="bg-black/30 rounded-lg p-4 relative cursor-crosshair" 
                style={{height: '400px'}}
                onClick={handleMapClick}
              >
                {/* 網格背景 */}
                <svg className="absolute inset-0 w-full h-full opacity-20">
                  <defs>
                    <pattern id="grid" width="20" height="20" patternUnits="userSpaceOnUse">
                      <path d="M 20 0 L 0 0 0 20" fill="none" stroke="gray" strokeWidth="1"/>
                    </pattern>
                  </defs>
                  <rect width="100%" height="100%" fill="url(#grid)" />
                </svg>

                {/* 障礙物 */}
                {obstacles.map(obstacle => (
                  <div
                    key={obstacle.id}
                    className="absolute bg-red-500/50 border border-red-400 rounded"
                    style={{
                      left: `${obstacle.x}px`,
                      top: `${obstacle.y}px`,
                      width: `${obstacle.width}px`,
                      height: `${obstacle.height}px`
                    }}
                  />
                ))}

                {/* 站點 */}
                {stations.map(station => (
                  <div
                    key={station.id}
                    className={`absolute w-6 h-6 ${station.color} rounded-lg border-2 border-white transform -translate-x-3 -translate-y-3 cursor-pointer hover:scale-110 transition-all`}
                    style={{
                      left: `${station.x}px`,
                      top: `${station.y}px`
                    }}
                    onClick={(e) => {
                      e.stopPropagation();
                      if (currentMode === 'control') {
                        goToStation(station);
                      }
                    }}
                    title={station.name}
                  >
                    {station.type === 'charging' ? (
                      <Home className="w-4 h-4 text-white m-0.5" />
                    ) : (
                      <Flag className="w-4 h-4 text-white m-0.5" />
                    )}
                  </div>
                ))}

                {/* 航點和路徑 */}
                {waypoints.length > 1 && (
                  <svg className="absolute inset-0 w-full h-full">
                    {waypoints.map((waypoint, index) => {
                      const nextWaypoint = waypoints[index + 1] || waypoints[0];
                      return (
                        <line
                          key={`path-${index}`}
                          x1={waypoint.x}
                          y1={waypoint.y}
                          x2={nextWaypoint.x}
                          y2={nextWaypoint.y}
                          stroke="#10b981"
                          strokeWidth="2"
                          strokeDasharray="5,5"
                          opacity="0.7"
                        />
                      );
                    })}
                  </svg>
                )}

                {waypoints.map((waypoint, index) => (
                  <div
                    key={waypoint.id}
                    className={`absolute w-5 h-5 rounded-full border-2 border-white transform -translate-x-2.5 -translate-y-2.5 cursor-pointer flex items-center justify-center text-xs font-bold ${
                      index === currentWaypointIndex && isPatrolling ? 'bg-yellow-400 animate-pulse' : 'bg-green-500'
                    }`}
                    style={{
                      left: `${waypoint.x}px`,
                      top: `${waypoint.y}px`
                    }}
                    onClick={(e) => {
                      e.stopPropagation();
                      if (currentMode === 'waypoints') {
                        deleteWaypoint(waypoint.id);
                      }
                    }}
                  >
                    {waypoint.order}
                  </div>
                ))}

                {/* 機器人當前位置 */}
                <div 
                  className="absolute w-4 h-4 bg-blue-500 rounded-full border-2 border-white transform -translate-x-2 -translate-y-2 transition-all duration-100"
                  style={{
                    left: `${currentPosition.x}px`,
                    top: `${currentPosition.y}px`
                  }}
                >
                  <div className="absolute inset-0 bg-blue-500 rounded-full animate-ping opacity-75"></div>
                </div>

                {/* 目標位置 */}
                <div 
                  className="absolute w-3 h-3 bg-red-500 rounded-full border border-white transform -translate-x-1.5 -translate-y-1.5"
                  style={{
                    left: `${targetPosition.x}px`,
                    top: `${targetPosition.y}px`
                  }}
                ></div>

                {/* 路徑線 */}
                {isMoving && (
                  <svg className="absolute inset-0 w-full h-full">
                    <line 
                      x1={currentPosition.x} 
                      y1={currentPosition.y} 
                      x2={targetPosition.x} 
                      y2={targetPosition.y} 
                      stroke="#3b82f6" 
                      strokeWidth="2" 
                      strokeDasharray="5,5"
                      className="animate-pulse"
                    />
                  </svg>
                )}
              </div>

              <div className="flex justify-between text-xs text-gray-400 mt-2">
                <span>🔵 機器人 🔴 目標 🟢 航點</span>
                <span>🏠 充電站 🚩 工作站 🟥 障礙物</span>
              </div>
            </div>
          </div>

          {/* 控制面板 */}
          <div className="space-y-6">
            {/* 建圖和航點控制 */}
            {(currentMode === 'mapping' || currentMode === 'waypoints') && (
              <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
                <h2 className="text-xl font-semibold text-white mb-4">
                  {currentMode === 'mapping' ? '建圖工具' : '航點管理'}
                </h2>

                {currentMode === 'mapping' && (
                  <div className="space-y-3">
                    <input
                      type="text"
                      placeholder="輸入站點名稱"
                      value={newStationName}
                      onChange={(e) => setNewStationName(e.target.value)}
                      className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500"
                    />
                    <p className="text-sm text-gray-300">點擊地圖新增站點</p>
                  </div>
                )}

                {currentMode === 'waypoints' && (
                  <div className="space-y-3">
                    <button
                      onClick={startPatrol}
                      disabled={waypoints.length === 0 || isMoving}
                      className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg font-medium transition-all"
                    >
                      <Route className="w-5 h-5" />
                      {isPatrolling ? '巡邏中...' : '開始巡邏'}
                    </button>
                    <p className="text-sm text-gray-300">點擊地圖新增航點，點擊航點刪除</p>
                  </div>
                )}
              </div>
            )}

            {/* 方向控制 */}
            {currentMode === 'control' && (
              <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
                <h2 className="text-xl font-semibold text-white mb-4">方向控制</h2>

                <div className="grid grid-cols-3 gap-2 max-w-48 mx-auto mb-4">
                  <div></div>
                  <button 
                    onClick={() => moveRobot('up')}
                    disabled={isMoving}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowUp className="w-6 h-6 mx-auto" />
                  </button>
                  <div></div>

                  <button 
                    onClick={() => moveRobot('left')}
                    disabled={isMoving}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowLeft className="w-6 h-6 mx-auto" />
                  </button>
                  <button 
                    onClick={stopRobot}
                    className="p-3 bg-red-500 hover:bg-red-600 text-white rounded-lg transition-all"
                  >
                    <Square className="w-6 h-6 mx-auto" />
                  </button>
                  <button 
                    onClick={() => moveRobot('right')}
                    disabled={isMoving}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowRight className="w-6 h-6 mx-auto" />
                  </button>

                  <div></div>
                  <button 
                    onClick={() => moveRobot('down')}
                    disabled={isMoving}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowDown className="w-6 h-6 mx-auto" />
                  </button>
                  <div></div>
                </div>

                <div className="space-y-3">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      移動速度: {speed}%
                    </label>
                    <input
                      type="range"
                      min="10"
                      max="100"
                      value={speed}
                      onChange={(e) => setSpeed(Number(e.target.value))}
                      className="w-full h-2 bg-white/20 rounded-lg appearance-none slider"
                    />
                  </div>

                  <button
                    onClick={goToPosition}
                    disabled={isMoving}
                    className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-purple-500 hover:bg-purple-600 disabled:bg-gray-600 text-white rounded-lg font-medium transition-all"
                  >
                    <Target className="w-5 h-5" />
                    導航至目標點
                  </button>
                </div>
              </div>
            )}

            {/* 站點列表 */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h2 className="text-xl font-semibold text-white mb-4">站點管理</h2>

              <div className="space-y-2 max-h-40 overflow-y-auto">
                {stations.map(station => (
                  <div key={station.id} className="flex items-center justify-between p-2 bg-white/10 rounded-lg">
                    <div className="flex items-center gap-2">
                      <div className={`w-3 h-3 ${station.color} rounded`}></div>
                      <span className="text-white text-sm">{station.name}</span>
                    </div>
                    <div className="flex gap-1">
                      <button
                        onClick={() => goToStation(station)}
                        disabled={isMoving}
                        className="p-1 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded text-xs transition-all"
                      >
                        前往
                      </button>
                      <button
                        onClick={() => deleteStation(station.id)}
                        className="p-1 bg-red-500 hover:bg-red-600 text-white rounded text-xs transition-all"
                      >
                        <Trash2 className="w-3 h-3" />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* 系統日誌 */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h2 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                <Clock className="w-5 h-5" />
                系統日誌
              </h2>

              <div className="space-y-2 max-h-32 overflow-y-auto">
                {logs.map((log, index) => (
                  <div key={index} className="flex items-start gap-3 text-sm">
                    <span className="text-gray-400 font-mono text-xs">{log.time}</span>
                    {log.type === 'success' && <CheckCircle className="w-4 h-4 text-green-400 mt-0.5" />}
                    {log.type === 'warning' && <AlertTriangle className="w-4 h-4 text-yellow-400 mt-0.5" />}
                    {log.type === 'info' && <Zap className="w-4 h-4 text-blue-400 mt-0.5" />}
                    {log.type === 'error' && <AlertTriangle className="w-4 h-4 text-red-400 mt-0.5" />}
                    <span className="text-gray-300 text-xs">{log.message}</span>
                  </div>
                ))}
              </div>
            </div>

            {/* 快速動作 */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h2 className="text-xl font-semibold text-white mb-4">快速動作</h2>

              <div className="grid grid-cols-1 gap-2">
                <button 
                  onClick={() => {
                    const chargingStation = stations.find(s => s.type === 'charging');
                    if (chargingStation) {
                      goToStation(chargingStation);
                    }
                  }}
                  disabled={isMoving || !stations.find(s => s.type === 'charging')}
                  className="flex items-center justify-center gap-2 px-3 py-2 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                >
                  <Home className="w-4 h-4" />
                  回充電站
                </button>
                <button 
                  onClick={startPatrol}
                  disabled={waypoints.length === 0 || isMoving}
                  className="flex items-center justify-center gap-2 px-3 py-2 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                >
                  {isPatrolling ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
                  {isPatrolling ? '暫停巡邏' : '開始巡邏'}
                </button>
                <button 
                  onClick={startMapping}
                  className={`flex items-center justify-center gap-2 px-3 py-2 rounded-lg text-sm transition-all ${
                    isMappingMode ? 'bg-purple-600 text-white' : 'bg-purple-500 hover:bg-purple-600 text-white'
                  }`}
                >
                  <Map className="w-4 h-4" />
                  {isMappingMode ? '結束建圖' : '開始建圖'}
                </button>
                <button className="flex items-center justify-center gap-2 px-3 py-2 bg-orange-500 hover:bg-orange-600 text-white rounded-lg text-sm transition-all">
                  <Camera className="w-4 h-4" />
                  開啟攝影機
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AMRDeveloperInterface;
