    addLog('info', 'ROS è©±é¡Œè¨­ç½®å®Œæˆ');
  };

  // ç¹ªè£½åœ°åœ–åˆ° Canvas
  const drawMap = (mapMessage) => {
    const canvas = mapCanvasRef.current;
    if (!canvas || !mapMessage) return;

    const ctx = canvas.getContext('2d');
    const { width, height, data } = mapMessage.info;

    // èª¿æ•´ canvas å°ºå¯¸
    const displayWidth = canvas.clientWidth;
    const displayHeight = canvas.clientHeight;
    canvas.width = displayWidth;
    canvas.height = displayHeight;

    // æ¸…ç©ºç•«å¸ƒ
    ctx.fillStyle = '#1f2937';
    ctx.fillRect(0, 0, displayWidth, displayHeight);

    // è¨ˆç®—ç¸®æ”¾æ¯”ä¾‹
    const scaleX = displayWidth / width;
    const scaleY = displayHeight / height;
    const scale = Math.min(scaleX, scaleY);

    // è¨ˆç®—åç§»é‡ï¼ˆå±…ä¸­é¡¯ç¤ºï¼‰
    const offsetX = (displayWidth - width * scale) / 2;
    const offsetY = (displayHeight - height * scale) / 2;

    // ç¹ªè£½åœ°åœ–æ•¸æ“š
    const imageData = ctx.createImageData(Math.floor(width * scale), Math.floor(height * scale));

    for (let y = 0; y < height; y++) {
      for (let x = 0; x < width; x++) {
        const dataIndex = y * width + x;
        const value = data[dataIndex];

        let color;
        if (value === -1) {
          color = [128, 128, 128, 255]; // æœªçŸ¥å€åŸŸ (ç°è‰²)
        } else if (value === 0) {
          color = [255, 255, 255, 255]; // ç©ºé–’å€åŸŸ (ç™½è‰²)
        } else if (value > 50) {
          color = [0, 0, 0, 255]; // éšœç¤™ç‰© (é»‘è‰²)
        } else {
          color = [200, 200, 200, 255]; // éƒ¨åˆ†å·²çŸ¥å€åŸŸ
        }

        // å°‡åƒç´ å¯«å…¥ç¸®æ”¾å¾Œçš„åœ–åƒ
        const scaledX = Math.floor(x * scale);
        const scaledY = Math.floor(y * scale);

        if (scaledX < imageData.width && scaledY < imageData.height) {
          const pixelIndex = (scaledY * imageData.width + scaledX) * 4;
          imageData.data[pixelIndex] = color[0];     // R
          imageData.data[pixelIndex + 1] = color[1]; // G
          imageData.data[pixelIndex + 2] = color[2]; // B
          imageData.data[pixelIndex + 3] = color[3]; // A
        }
      }
    }

    // å°‡åœ–åƒæ•¸æ“šç¹ªè£½åˆ°ç•«å¸ƒ
    ctx.putImageData(imageData, offsetX, offsetY);

    // ç¹ªè£½æ©Ÿå™¨äººä½ç½®
    if (currentPose) {
      const resolution = mapMessage.info.resolution;
      const origin = mapMessage.info.origin.position;

      // è¨ˆç®—æ©Ÿå™¨äººåœ¨åœ°åœ–ä¸­çš„åƒç´ ä½ç½®
      const robotMapX = (currentPose.x - origin.x) / resolution;
      const robotMapY = height - (currentPose.y - origin.y) / resolution;

      // è½‰æ›åˆ°é¡¯ç¤ºåæ¨™
      const robotDisplayX = offsetX + robotMapX * scale;
      const robotDisplayY = offsetY + robotMapY * scale;

      // ç¹ªè£½æ©Ÿå™¨äºº
      ctx.fillStyle = '#3b82f6';
      ctx.beginPath();
      ctx.arc(robotDisplayX, robotDisplayY, 8, 0, 2 * Math.PI);
      ctx.fill();

      // ç¹ªè£½æ©Ÿå™¨äººæ–¹å‘
      const theta = parseFloat(currentPose.theta) * Math.PI / 180;
      const arrowLength = 15;
      const arrowX = robotDisplayX + Math.cos(theta) * arrowLength;
      const arrowY = robotDisplayY - Math.sin(theta) * arrowLength;

      ctx.strokeStyle = '#3b82f6';
      ctx.lineWidth = 3;
      ctx.beginPath();
      ctx.moveTo(robotDisplayX, robotDisplayY);
      ctx.lineTo(arrowX, arrowY);
      ctx.stroke();

      // ç¹ªè£½æ©Ÿå™¨äººå‘¨åœçš„æƒæç¯„åœ
      ctx.strokeStyle = '#10b981';
      ctx.lineWidth = 1;
      ctx.setLineDash([5, 5]);
      ctx.beginPath();
      ctx.arc(robotDisplayX, robotDisplayY, 30, 0, 2 * Math.PI);
      ctx.stroke();
      ctx.setLineDash([]);
    }

    // ç¹ªè£½ç›®æ¨™ä½ç½®ï¼ˆå¦‚æœæœ‰ï¼‰
    if (goalPose) {
      const resolution = mapMessage.info.resolution;
      const origin = mapMessage.info.origin.position;

      const goalMapX = (goalPose.x - origin.x) / resolution;
      const goalMapY = height - (goalPose.y - origin.y) / resolution;

      const goalDisplayX = offsetX + goalMapX * scale;
      const goalDisplayY = offsetY + goalMapY * scale;

      ctx.fillStyle = '#ef4444';
      ctx.beginPath();
      ctx.arc(goalDisplayX, goalDisplayY, 6, 0, 2 * Math.PI);
      ctx.fill();
    }

    // åœ¨å»ºåœ–æ¨¡å¼ä¸‹é¡¯ç¤ºé¡å¤–ä¿¡æ¯
    if (currentMode === 'mapping') {
