    publishCmdVel(linear, angular);
    addLog('info', `ç§»å‹•æŒ‡ä»¤: ç·šé€Ÿåº¦=${linear}, è§’é€Ÿåº¦=${angular}`);
  };

  // é–‹å§‹/åœæ­¢å»ºåœ–
  const toggleMapping = () => {
