import React, { useState, useEffect, useRef } from 'react';
import { 
  Square, 
  ArrowUp, 
  ArrowDown, 
  ArrowLeft, 
  ArrowRight,
  Battery,
  Camera,
  MapPin,
  Activity,
  Zap,
  AlertTriangle,
  CheckCircle,
  Clock,
  Wifi,
  WifiOff,
  Save,
  RotateCw,
  RotateCcw
} from 'lucide-react';

// 從全局變量獲取 ROSLIB (CDN 方式)
const ROSLIB = window.ROSLIB || (() => {
  console.warn('ROSLIB 未加載，使用模擬模式');
  return {
    Ros: class {
      constructor() {
        this.isConnected = false;
      }
      connect() {
        console.log('模擬 ROS 連接');
        setTimeout(() => {
          this.isConnected = true;
          if (this.onConnection) this.onConnection();
        }, 1000);
      }
      close() {
        this.isConnected = false;
      }
      on() {}
    },
    Topic: class {
      publish() {}
      subscribe() {}
    },
    Service: class {
      callService() {}
    },
    Message: class {
      constructor(data) {
        Object.assign(this, data);
      }
    }
  };
})();

const TurtleBotInterface = () => {
  // ROS 連接狀態
  const [ros, setRos] = useState(null);
  const [rosConnected, setRosConnected] = useState(false);
  const [rosUrl, setRosUrl] = useState('ws://localhost:9090');

  // 機器人狀態
  const [robotStatus, setRobotStatus] = useState('未連接');
  const [batteryLevel, setBatteryLevel] = useState(85);
  const [currentPose, setCurrentPose] = useState({ x: 0, y: 0, theta: 0 });
  const [linearVel, setLinearVel] = useState(0);
  const [angularVel] = useState(0);
  const [speed, setSpeed] = useState(0.2);
  const [angularSpeed, setAngularSpeed] = useState(0.5);

  // 地圖和導航
  const [currentMode, setCurrentMode] = useState('teleop'); // teleop, mapping, navigation
  const [mapData, setMapData] = useState(null);
  const [goalPose, setGoalPose] = useState(null);
  const [isMappingActive, setIsMappingActive] = useState(false);

  // UI 狀態
  const [logs, setLogs] = useState([
    { time: new Date().toLocaleTimeString(), type: 'info', message: 'TurtleBot3 界面啟動' }
  ]);

  const mapCanvasRef = useRef(null);
  const cmdVelRef = useRef(null);
  const mapSubRef = useRef(null);
  const odomSubRef = useRef(null);

  // 初始化 ROS 連接
  useEffect(() => {
    // 檢查 ROSLIB 是否可用
    if (window.ROSLIB) {
      addLog('success', 'ROSLIB 已成功加載');
    } else {
      addLog('warning', 'ROSLIB 未加載，使用模擬模式');
    }

    const initAndSetupROS = () => {
      try {
        const rosInstance = new ROSLIB.Ros({
          url: rosUrl
        });

        rosInstance.on('connection', () => {
          setRosConnected(true);
          setRobotStatus('已連接');
          addLog('success', 'ROS 連接成功');
          setupRosTopics(rosInstance);
        });

        rosInstance.on('error', (error) => {
          setRosConnected(false);
          setRobotStatus('連接錯誤');
          addLog('error', `ROS 連接錯誤: ${error}`);
        });

        rosInstance.on('close', () => {
          setRosConnected(false);
          setRobotStatus('連接斷開');
          addLog('warning', 'ROS 連接斷開');
        });

        if (typeof rosInstance.connect === 'function') {
          rosInstance.connect();
        }

        setRos(rosInstance);
      } catch (error) {
        addLog('error', `初始化失敗: ${error.message}`);
      }
    };

    initAndSetupROS();

    return () => {
      // 清理函數
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const addLog = (type, message) => {
    const time = new Date().toLocaleTimeString();
    setLogs(prev => [...prev.slice(-9), { time, type, message }]);
  };

  const initROS = () => {
    if (!ros || !ros.isConnected) {
      // 重新初始化 ROS 連接
      window.location.reload();
    }
  };

  const setupRosTopics = (rosInstance) => {
    // cmd_vel 發布者 (控制機器人移動)
    cmdVelRef.current = new ROSLIB.Topic({
      ros: rosInstance,
      name: '/cmd_vel',
      messageType: 'geometry_msgs/Twist'
    });

    // odom 訂閱者 (機器人位置)
    odomSubRef.current = new ROSLIB.Topic({
      ros: rosInstance,
      name: '/odom',
      messageType: 'nav_msgs/Odometry'
    });

    odomSubRef.current.subscribe((message) => {
      const { position, orientation } = message.pose.pose;
      const { linear } = message.twist.twist;

      // 轉換四元數到歐拉角
      const theta = 2 * Math.atan2(orientation.z, orientation.w);

      setCurrentPose({
        x: parseFloat(position.x.toFixed(2)),
        y: parseFloat(position.y.toFixed(2)),
        theta: (theta * 180 / Math.PI).toFixed(1)
      });

      setLinearVel(linear.x.toFixed(2));
    });

    // 地圖訂閱者 - 完全重寫
    mapSubRef.current = new ROSLIB.Topic({
      ros: rosInstance,
      name: '/map',
      messageType: 'nav_msgs/OccupancyGrid'
    });

    addLog('info', '正在訂閱地圖話題 /map...');

    mapSubRef.current.subscribe((message) => {
      console.log('🗺️ 收到地圖數據!', {
        width: message.info.width,
        height: message.info.height,
        resolution: message.info.resolution,
        dataLength: message.data ? message.data.length : 0
      });

      if (message.data && message.data.length > 0) {
        setMapData(message);
        // 延遲一點再繪製，確保狀態更新
        setTimeout(() => drawMap(message), 100);
        addLog('success', `地圖更新: ${message.info.width}x${message.info.height}, 解析度: ${message.info.resolution.toFixed(3)}m`);
      } else {
        addLog('warning', '收到地圖訊息但無數據');
      }
    });

    // 模擬電池狀態（因為 TurtleBot3 模擬器沒有真實電池）
    const batterySimulation = setInterval(() => {
      setBatteryLevel(prev => {
        const newLevel = Math.max(20, prev - 0.1);
        return newLevel;
      });
    }, 10000);

    // 清理函數中清除定時器
    rosInstance.batterySimulation = batterySimulation;

    addLog('success', 'ROS 話題設置完成');
  };

  // 繪製地圖到 Canvas - 修復版本
  const drawMap = (mapMessage) => {
    const canvas = mapCanvasRef.current;
    if (!canvas || !mapMessage) {
      console.log('❌ Canvas 或地圖數據不存在');
      return;
    }

    console.log('🎨 開始繪製地圖...', {
      canvas: !!canvas,
      mapData: !!mapMessage,
      width: mapMessage.info?.width,
      height: mapMessage.info?.height,
      zoom: mapZoom
    });

    const ctx = canvas.getContext('2d');
    const { width, height, resolution, origin } = mapMessage.info;
    const { data } = mapMessage;

    if (!data || data.length === 0) {
      console.log('❌ 地圖數據為空');
      return;
    }

    // 設置 canvas 尺寸
    const displayWidth = canvas.clientWidth || 800;
    const displayHeight = canvas.clientHeight || 400;
    canvas.width = displayWidth;
    canvas.height = displayHeight;

    // 清空畫布
    ctx.fillStyle = '#374151'; // 深灰色背景
    ctx.fillRect(0, 0, displayWidth, displayHeight);

    // 計算基礎縮放比例
    const baseScaleX = displayWidth / width;
    const baseScaleY = displayHeight / height;
    const baseScale = Math.min(baseScaleX, baseScaleY) * 0.8;

    // 應用用戶縮放
    const finalScale = baseScale * mapZoom;

    // 計算偏移量（考慮用戶拖拽和縮放）
    const centerX = displayWidth / 2;
    const centerY = displayHeight / 2;
    const offsetX = centerX - (width * finalScale) / 2 + mapOffset.x;
    const offsetY = centerY - (height * finalScale) / 2 + mapOffset.y;

    console.log('📏 繪製參數:', { 
      finalScale, 
      offsetX, 
      offsetY, 
      mapZoom,
      baseScale 
    });

    // 直接繪製像素
    const imageData = ctx.createImageData(Math.ceil(width * finalScale), Math.ceil(height * finalScale));

    for (let y = 0; y < height; y++) {
      for (let x = 0; x < width; x++) {
        const dataIndex = y * width + x;
        const value = data[dataIndex];

        let color;
        if (value === -1) {
          color = [128, 128, 128, 255]; // 未知區域 (灰色)
        } else if (value >= 0 && value < 25) {
          color = [240, 240, 240, 255]; // 空閒區域 (白色)
        } else if (value >= 65) {
          color = [20, 20, 20, 255]; // 障礙物 (黑色)
        } else {
          color = [180, 180, 180, 255]; // 部分已知區域
        }

        // 將像素寫入縮放後的圖像
        const scaledX = Math.floor(x * finalScale);
        const scaledY = Math.floor(y * finalScale);

        if (scaledX < imageData.width && scaledY < imageData.height) {
          const pixelIndex = (scaledY * imageData.width + scaledX) * 4;
          if (pixelIndex + 3 < imageData.data.length) {
            imageData.data[pixelIndex] = color[0];
            imageData.data[pixelIndex + 1] = color[1];
            imageData.data[pixelIndex + 2] = color[2];
            imageData.data[pixelIndex + 3] = color[3];
          }
        }
      }
    }

    // 將圖像數據繪製到畫布
    ctx.putImageData(imageData, offsetX, offsetY);

    // 修正機器人位置計算
    if (currentPose && currentPose.x !== undefined) {
      // 正確的坐標變換
      const robotWorldX = parseFloat(currentPose.x);
      const robotWorldY = parseFloat(currentPose.y);

      // 將世界坐標轉換為地圖像素坐標
      const robotMapX = (robotWorldX - origin.position.x) / resolution;
      const robotMapY = (origin.position.y + height * resolution - robotWorldY) / resolution;

      // 轉換到顯示坐標
      const robotDisplayX = offsetX + robotMapX * finalScale;
      const robotDisplayY = offsetY + robotMapY * finalScale;

      console.log('🤖 機器人位置計算:', {
        world: { x: robotWorldX, y: robotWorldY },
        map: { x: robotMapX, y: robotMapY },
        display: { x: robotDisplayX, y: robotDisplayY },
        origin: origin.position
      });

      // 確保機器人位置在合理範圍內再繪製
      if (robotDisplayX >= -50 && robotDisplayX <= displayWidth + 50 && 
          robotDisplayY >= -50 && robotDisplayY <= displayHeight + 50) {

        // 繪製機器人位置（藍色圓點）
        ctx.fillStyle = '#3b82f6';
        ctx.strokeStyle = '#1e40af';
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.arc(robotDisplayX, robotDisplayY, 8 * mapZoom, 0, 2 * Math.PI);
        ctx.fill();
        ctx.stroke();

        // 繪製機器人方向箭頭
        const theta = parseFloat(currentPose.theta) * Math.PI / 180;
        const arrowLength = 20 * mapZoom;
        const arrowX = robotDisplayX + Math.cos(theta) * arrowLength;
        const arrowY = robotDisplayY - Math.sin(theta) * arrowLength;

        ctx.strokeStyle = '#1d4ed8';
        ctx.lineWidth = 3;
        ctx.beginPath();
        ctx.moveTo(robotDisplayX, robotDisplayY);
        ctx.lineTo(arrowX, arrowY);
        ctx.stroke();

        // 繪製掃描範圍
        ctx.strokeStyle = '#10b981';
        ctx.lineWidth = 2;
        ctx.setLineDash([5, 5]);
        ctx.beginPath();
        ctx.arc(robotDisplayX, robotDisplayY, 40 * mapZoom, 0, 2 * Math.PI);
        ctx.stroke();
        ctx.setLineDash([]);
      }
    }

    // 繪製目標位置（如果有）
    if (goalPose) {
      const goalMapX = (goalPose.x - origin.position.x) / resolution;
      const goalMapY = (origin.position.y + height * resolution - goalPose.y) / resolution;

      const goalDisplayX = offsetX + goalMapX * finalScale;
      const goalDisplayY = offsetY + goalMapY * finalScale;

      ctx.fillStyle = '#ef4444';
      ctx.beginPath();
      ctx.arc(goalDisplayX, goalDisplayY, 6 * mapZoom, 0, 2 * Math.PI);
      ctx.fill();
    }

    // 添加地圖信息顯示
    ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
    ctx.fillRect(10, 10, 280, 100);
    ctx.fillStyle = 'white';
    ctx.font = '12px monospace';
    ctx.fillText(`地圖尺寸: ${width} x ${height}`, 20, 30);
    ctx.fillText(`解析度: ${resolution.toFixed(3)} m/pixel`, 20, 50);
    ctx.fillText(`機器人: (${currentPose.x}, ${currentPose.y})`, 20, 70);
    ctx.fillText(`縮放: ${(mapZoom * 100).toFixed(0)}%`, 20, 90);

    console.log('✅ 地圖繪製完成');
  };

  // 發送速度命令
  const publishCmdVel = (linear, angular) => {
    if (!rosConnected || !cmdVelRef.current) return;

    const twist = new ROSLIB.Message({
      linear: { x: linear, y: 0, z: 0 },
      angular: { x: 0, y: 0, z: angular }
    });

    cmdVelRef.current.publish(twist);
  };

  // 停止機器人
  const stopRobot = () => {
    publishCmdVel(0, 0);
    addLog('warning', '機器人停止');
  };

  // 鍵盤控制
  const moveRobot = (direction) => {
    if (!rosConnected) {
      addLog('error', '請先連接 ROS');
      return;
    }

    let linear = 0, angular = 0;

    switch (direction) {
      case 'forward':
        linear = speed;
        break;
      case 'backward':
        linear = -speed;
        break;
      case 'left':
        angular = angularSpeed;
        break;
      case 'right':
        angular = -angularSpeed;
        break;
      case 'rotate_left':
        angular = angularSpeed;
        break;
      case 'rotate_right':
        angular = -angularSpeed;
        break;
      default:
        break;
    }

    publishCmdVel(linear, angular);
    addLog('info', `移動指令: 線速度=${linear}, 角速度=${angular}`);
  };

  // 開始/停止建圖
  const toggleMapping = () => {
    if (!rosConnected) {
      addLog('error', '請先連接 ROS');
      return;
    }

    if (isMappingActive) {
      // 停止建圖並保存地圖
      const saveMapService = new ROSLIB.Service({
        ros: ros,
        name: '/dynamic_map',
        serviceType: 'nav_msgs/GetMap'
      });

      saveMapService.callService(new ROSLIB.ServiceRequest(), (result) => {
        addLog('success', '地圖已保存');
      });

      setIsMappingActive(false);
      setRobotStatus('建圖完成');
    } else {
      setIsMappingActive(true);
      setRobotStatus('建圖中...');
      addLog('info', '開始 SLAM 建圖');
    }
  };

  // 設置導航目標
  const setNavigationGoal = (x, y) => {
    if (!rosConnected) {
      addLog('error', '請先連接 ROS');
      return;
    }

    const goalTopic = new ROSLIB.Topic({
      ros: ros,
      name: '/move_base_simple/goal',
      messageType: 'geometry_msgs/PoseStamped'
    });

    const goal = new ROSLIB.Message({
      header: {
        frame_id: 'map',
        stamp: { sec: 0, nsec: 0 }
      },
      pose: {
        position: { x: x, y: y, z: 0 },
        orientation: { x: 0, y: 0, z: 0, w: 1 }
      }
    });

    goalTopic.publish(goal);
    setGoalPose({ x, y });
    addLog('info', `設置導航目標: (${x.toFixed(2)}, ${y.toFixed(2)})`);
  };

  // 地圖點擊處理
  const handleMapClick = (event) => {
    if (!mapData || currentMode !== 'navigation') return;

    const canvas = mapCanvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // 轉換畫布坐標到世界坐標
    const worldX = x * mapData.info.resolution + mapData.info.origin.position.x;
    const worldY = (canvas.height - y) * mapData.info.resolution + mapData.info.origin.position.y;

    setNavigationGoal(worldX, worldY);
  };

  // 滑鼠滾輪縮放處理
  const handleMapWheel = (event) => {
    event.preventDefault();
    const delta = event.deltaY > 0 ? 0.9 : 1.1;
    const newZoom = Math.max(0.5, Math.min(5.0, mapZoom * delta));
    setMapZoom(newZoom);

    // 重新繪製地圖
    if (mapData) {
      setTimeout(() => drawMap(mapData), 10);
    }
  };

  // 滑鼠拖拽處理
  const handleMapMouseDown = (event) => {
    setIsDragging(true);
    setLastMousePos({ x: event.clientX, y: event.clientY });
  };

  const handleMapMouseMove = (event) => {
    if (!isDragging) return;

    const deltaX = event.clientX - lastMousePos.x;
    const deltaY = event.clientY - lastMousePos.y;

    setMapOffset(prev => ({
      x: prev.x + deltaX,
      y: prev.y + deltaY
    }));

    setLastMousePos({ x: event.clientX, y: event.clientY });

    // 重新繪製地圖
    if (mapData) {
      drawMap(mapData);
    }
  };

  const handleMapMouseUp = () => {
    setIsDragging(false);
  };

  // 重置地圖視圖
  const resetMapView = () => {
    setMapZoom(1.0);
    setMapOffset({ x: 0, y: 0 });
    if (mapData) {
      setTimeout(() => drawMap(mapData), 10);
    }
  };

  const getConnectionStatus = () => {
    return rosConnected ? 'text-green-400' : 'text-red-400';
  };

  const getBatteryColor = () => {
    if (batteryLevel > 60) return 'text-green-400';
    if (batteryLevel > 30) return 'text-yellow-400';
    return 'text-red-400';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 p-6">
      <div className="max-w-7xl mx-auto">
        {/* 標題 */}
        <div className="text-center mb-6">
          <h1 className="text-4xl font-bold text-white mb-2">TurtleBot3 控制界面</h1>
          <p className="text-gray-300">ROS Noetic · SLAM · 導航系統</p>
        </div>

        {/* ROS 連接設定 */}
        <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-4 mb-6 border border-white/20">
          <div className="flex items-center gap-4">
            <div className="flex-1">
              <label className="block text-sm font-medium text-gray-300 mb-2">ROS Bridge URL</label>
              <input
                type="text"
                value={rosUrl}
                onChange={(e) => setRosUrl(e.target.value)}
                className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white placeholder-gray-400"
                placeholder="ws://localhost:9090"
              />
            </div>
            <button
              onClick={initROS}
              disabled={rosConnected}
              className="px-6 py-2 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
            >
              {rosConnected ? '已連接' : '連接 ROS'}
            </button>
          </div>
        </div>

        {/* 模式切換 */}
        <div className="flex justify-center gap-2 mb-6">
          <button
            onClick={() => setCurrentMode('teleop')}
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              currentMode === 'teleop' ? 'bg-blue-500 text-white' : 'bg-white/10 text-gray-300'
            }`}
          >
            遙控模式
          </button>
          <button
            onClick={() => setCurrentMode('mapping')}
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              currentMode === 'mapping' ? 'bg-purple-500 text-white' : 'bg-white/10 text-gray-300'
            }`}
          >
            建圖模式
          </button>
          <button
            onClick={() => setCurrentMode('navigation')}
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              currentMode === 'navigation' ? 'bg-green-500 text-white' : 'bg-white/10 text-gray-300'
            }`}
          >
            導航模式
          </button>
        </div>

        {/* 狀態概覽 */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              {rosConnected ? <Wifi className="w-8 h-8 text-green-400" /> : <WifiOff className="w-8 h-8 text-red-400" />}
              <div>
                <p className="text-sm text-gray-300">ROS 狀態</p>
                <p className={`font-semibold ${getConnectionStatus()}`}>{robotStatus}</p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              <Battery className={`w-8 h-8 ${getBatteryColor()}`} />
              <div>
                <p className="text-sm text-gray-300">電量</p>
                <p className={`font-semibold ${getBatteryColor()}`}>{batteryLevel}%</p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              <MapPin className="w-8 h-8 text-purple-400" />
              <div>
                <p className="text-sm text-gray-300">位置</p>
                <p className="font-semibold text-purple-400">
                  ({currentPose.x}, {currentPose.y})
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center gap-3">
              <Activity className="w-8 h-8 text-orange-400" />
              <div>
                <p className="text-sm text-gray-300">速度</p>
                <p className="font-semibold text-orange-400">{linearVel} m/s</p>
              </div>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* 地圖顯示 */}
          <div className="lg:col-span-2">
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold text-white flex items-center gap-2">
                  <MapPin className="w-5 h-5" />
                  {currentMode === 'mapping' ? 'SLAM 即時建圖' : currentMode === 'navigation' ? '導航地圖' : '機器人地圖'}
                </h2>
                <div className="text-sm text-gray-300">
                  {currentMode === 'navigation' && '點擊地圖設置目標點'}
                  {currentMode === 'mapping' && (isMappingActive ? '即時建圖中...' : '準備開始建圖')}
                  {currentMode === 'teleop' && '遙控模式'}
                </div>
              </div>

              <div className="bg-black/30 rounded-lg p-4 relative">
                <canvas
                  ref={mapCanvasRef}
                  onClick={handleMapClick}
                  onWheel={handleMapWheel}
                  onMouseDown={handleMapMouseDown}
                  onMouseMove={handleMapMouseMove}
                  onMouseUp={handleMapMouseUp}
                  onMouseLeave={handleMapMouseUp}
                  className="w-full h-96 bg-gray-800 rounded cursor-move border-2 border-dashed border-gray-600"
                  style={{ maxWidth: '100%', height: '400px' }}
                />

                {/* 地圖控制按鈕 */}
                <div className="absolute top-4 right-4 flex flex-col gap-2">
                  <button
                    onClick={resetMapView}
                    className="px-3 py-1 bg-blue-500/80 hover:bg-blue-600/80 text-white rounded text-sm transition-all"
                    title="重置視圖"
                  >
                    🔄 重置
                  </button>
                  <div className="px-3 py-1 bg-black/60 text-white rounded text-sm">
                    {(mapZoom * 100).toFixed(0)}%
                  </div>
                </div>

                {!mapData && (
                  <div className="absolute inset-0 flex flex-col items-center justify-center">
                    <div className="text-gray-400 text-center">
                      {currentMode === 'mapping' ? (
                        <>
                          <MapPin className="w-12 h-12 mx-auto mb-2 opacity-50" />
                          <p className="text-lg mb-2">等待 SLAM 地圖數據...</p>
                          <p className="text-sm">請先啟動 SLAM 並開始建圖</p>
                          <div className="mt-4 p-3 bg-purple-500/20 rounded-lg">
                            <p className="text-xs text-purple-200">
                              終端執行：roslaunch turtlebot3_slam turtlebot3_slam.launch
                            </p>
                          </div>
                        </>
                      ) : (
                        <>
                          <MapPin className="w-12 h-12 mx-auto mb-2 opacity-50" />
                          <p>等待地圖數據...</p>
                        </>
                      )}
                    </div>
                  </div>
                )}

                {/* 地圖數據載入指示器 */}
                {currentMode === 'mapping' && isMappingActive && !mapData && (
                  <div className="absolute top-4 left-4">
                    <div className="flex items-center gap-2 bg-purple-500/80 text-white px-3 py-2 rounded-lg text-sm">
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                      正在等待地圖數據
                    </div>
                  </div>
                )}

                {/* 機器人位置指示 */}
                {mapData && currentPose && (
                  <div className="absolute bottom-4 left-4 bg-blue-500/80 text-white px-3 py-2 rounded-lg text-sm">
                    機器人位置: ({currentPose.x}, {currentPose.y})
                  </div>
                )}
              </div>

              <div className="flex justify-between text-xs text-gray-400 mt-2">
                <span>🔵 機器人位置 🔴 目標點 🟢 雷射掃描</span>
                <span>
                  {currentMode === 'mapping' ? '🗺️ 滾輪縮放 · 拖拽移動' : 
                   currentMode === 'navigation' ? '🎯 點擊設定目標' : '🎮 遙控模式'}
                </span>
              </div>
            </div>
          </div>

          {/* 控制面板 */}
          <div className="space-y-6">
            {/* 遙控模式 */}
            {currentMode === 'teleop' && (
              <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
                <h2 className="text-xl font-semibold text-white mb-4">鍵盤控制</h2>

                <div className="grid grid-cols-3 gap-2 max-w-48 mx-auto mb-4">
                  <div></div>
                  <button 
                    onMouseDown={() => moveRobot('forward')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowUp className="w-6 h-6 mx-auto" />
                  </button>
                  <div></div>

                  <button 
                    onMouseDown={() => moveRobot('left')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowLeft className="w-6 h-6 mx-auto" />
                  </button>
                  <button 
                    onClick={stopRobot}
                    className="p-3 bg-red-500 hover:bg-red-600 text-white rounded-lg transition-all"
                  >
                    <Square className="w-6 h-6 mx-auto" />
                  </button>
                  <button 
                    onMouseDown={() => moveRobot('right')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowRight className="w-6 h-6 mx-auto" />
                  </button>

                  <div></div>
                  <button 
                    onMouseDown={() => moveRobot('backward')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="p-3 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                  >
                    <ArrowDown className="w-6 h-6 mx-auto" />
                  </button>
                  <div></div>
                </div>

                <div className="flex gap-2 mb-4">
                  <button 
                    onMouseDown={() => moveRobot('rotate_left')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="flex-1 flex items-center justify-center gap-2 p-2 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                  >
                    <RotateCcw className="w-4 h-4" />
                    左轉
                  </button>
                  <button 
                    onMouseDown={() => moveRobot('rotate_right')}
                    onMouseUp={stopRobot}
                    disabled={!rosConnected}
                    className="flex-1 flex items-center justify-center gap-2 p-2 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                  >
                    <RotateCw className="w-4 h-4" />
                    右轉
                  </button>
                </div>

                <div className="space-y-3">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      線速度: {speed} m/s
                    </label>
                    <input
                      type="range"
                      min="0.1"
                      max="1.0"
                      step="0.1"
                      value={speed}
                      onChange={(e) => setSpeed(parseFloat(e.target.value))}
                      className="w-full h-2 bg-white/20 rounded-lg appearance-none slider"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      角速度: {angularSpeed} rad/s
                    </label>
                    <input
                      type="range"
                      min="0.1"
                      max="2.0"
                      step="0.1"
                      value={angularSpeed}
                      onChange={(e) => setAngularSpeed(parseFloat(e.target.value))}
                      className="w-full h-2 bg-white/20 rounded-lg appearance-none slider"
                    />
                  </div>
                </div>
              </div>
            )}

            {/* 建圖模式 */}
            {currentMode === 'mapping' && (
              <>
                {/* SLAM 建圖控制 */}
                <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20 mb-6">
                  <h2 className="text-xl font-semibold text-white mb-4">SLAM 建圖</h2>

                  <div className="space-y-4">
                    <button
                      onClick={toggleMapping}
                      disabled={!rosConnected}
                      className={`w-full flex items-center justify-center gap-2 px-4 py-3 ${
                        isMappingActive ? 'bg-red-500 hover:bg-red-600' : 'bg-purple-500 hover:bg-purple-600'
                      } disabled:bg-gray-600 text-white rounded-lg font-medium transition-all`}
                    >
                      <MapPin className="w-5 h-5" />
                      {isMappingActive ? '停止建圖' : '開始建圖'}
                    </button>

                    <button
                      disabled={!rosConnected || !mapData}
                      className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg font-medium transition-all"
                    >
                      <Save className="w-5 h-5" />
                      保存地圖
                    </button>
                  </div>
                </div>

                {/* 建圖時的方向控制 */}
                <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
                  <h2 className="text-xl font-semibold text-white mb-4">建圖控制</h2>

                  <div className="grid grid-cols-3 gap-2 max-w-48 mx-auto mb-4">
                    <div></div>
                    <button 
                      onMouseDown={() => moveRobot('forward')}
                      onMouseUp={stopRobot}
                      disabled={!rosConnected}
                      className="p-3 bg-purple-500 hover:bg-purple-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                    >
                      <ArrowUp className="w-6 h-6 mx-auto" />
                    </button>
                    <div></div>

                    <button 
                      onMouseDown={() => moveRobot('left')}
                      onMouseUp={stopRobot}
                      disabled={!rosConnected}
                      className="p-3 bg-purple-500 hover:bg-purple-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                    >
                      <ArrowLeft className="w-6 h-6 mx-auto" />
                    </button>
                    <button 
                      onClick={stopRobot}
                      className="p-3 bg-red-500 hover:bg-red-600 text-white rounded-lg transition-all"
                    >
                      <Square className="w-6 h-6 mx-auto" />
                    </button>
                    <button 
                      onMouseDown={() => moveRobot('right')}
                      onMouseUp={stopRobot}
                      disabled={!rosConnected}
                      className="p-3 bg-purple-500 hover:bg-purple-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                    >
                      <ArrowRight className="w-6 h-6 mx-auto" />
                    </button>

                    <div></div>
                    <button 
                      onMouseDown={() => moveRobot('backward')}
                      onMouseUp={stopRobot}
                      disabled={!rosConnected}
                      className="p-3 bg-purple-500 hover:bg-purple-600 disabled:bg-gray-600 text-white rounded-lg transition-all"
                    >
                      <ArrowDown className="w-6 h-6 mx-auto" />
                    </button>
                    <div></div>
                  </div>

                  <div className="flex gap-2 mb-4">
                    <button 
                      onMouseDown={() => moveRobot('rotate_left')}
                      onMouseUp={stopRobot}
                      disabled={!rosConnected}
                      className="flex-1 flex items-center justify-center gap-2 p-2 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                    >
                      <RotateCcw className="w-4 h-4" />
                      左轉
                    </button>
                    <button 
                      onMouseDown={() => moveRobot('rotate_right')}
                      onMouseUp={stopRobot}
                      disabled={!rosConnected}
                      className="flex-1 flex items-center justify-center gap-2 p-2 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                    >
                      <RotateCw className="w-4 h-4" />
                      右轉
                    </button>
                  </div>

                  <div className="space-y-3">
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">
                        建圖速度: {speed} m/s
                      </label>
                      <input
                        type="range"
                        min="0.1"
                        max="0.5"
                        step="0.1"
                        value={speed}
                        onChange={(e) => setSpeed(parseFloat(e.target.value))}
                        className="w-full h-2 bg-white/20 rounded-lg appearance-none slider"
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">
                        轉向速度: {angularSpeed} rad/s
                      </label>
                      <input
                        type="range"
                        min="0.2"
                        max="1.0"
                        step="0.1"
                        value={angularSpeed}
                        onChange={(e) => setAngularSpeed(parseFloat(e.target.value))}
                        className="w-full h-2 bg-white/20 rounded-lg appearance-none slider"
                      />
                    </div>
                  </div>

                  <div className="mt-4 p-3 bg-purple-500/20 rounded-lg">
                    <p className="text-sm text-purple-200">
                      💡 建圖提示：慢慢移動機器人，讓雷射掃描器收集環境數據
                    </p>
                  </div>
                </div>
              </>
            )}

            {/* 導航模式 */}
            {currentMode === 'navigation' && (
              <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
                <h2 className="text-xl font-semibold text-white mb-4">自主導航</h2>

                <div className="space-y-4">
                  {goalPose && (
                    <div className="bg-black/20 rounded-lg p-3">
                      <p className="text-sm text-gray-300">目標位置:</p>
                      <p className="text-white">({goalPose.x.toFixed(2)}, {goalPose.y.toFixed(2)})</p>
                    </div>
                  )}

                  <button
                    onClick={stopRobot}
                    disabled={!rosConnected}
                    className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-red-500 hover:bg-red-600 disabled:bg-gray-600 text-white rounded-lg font-medium transition-all"
                  >
                    <Square className="w-5 h-5" />
                    停止導航
                  </button>

                  <p className="text-sm text-gray-300">
                    點擊地圖設置導航目標點
                  </p>
                </div>
              </div>
            )}

            {/* 系統日誌 */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h2 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                <Clock className="w-5 h-5" />
                系統日誌
              </h2>

              <div className="space-y-2 max-h-48 overflow-y-auto">
                {logs.map((log, index) => (
                  <div key={index} className="flex items-start gap-3 text-sm">
                    <span className="text-gray-400 font-mono text-xs">{log.time}</span>
                    {log.type === 'success' && <CheckCircle className="w-4 h-4 text-green-400 mt-0.5" />}
                    {log.type === 'warning' && <AlertTriangle className="w-4 h-4 text-yellow-400 mt-0.5" />}
                    {log.type === 'info' && <Zap className="w-4 h-4 text-blue-400 mt-0.5" />}
                    {log.type === 'error' && <AlertTriangle className="w-4 h-4 text-red-400 mt-0.5" />}
                    <span className="text-gray-300 text-xs">{log.message}</span>
                  </div>
                ))}
              </div>
            </div>

            {/* 機器人資訊 */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h2 className="text-xl font-semibold text-white mb-4">機器人資訊</h2>

              <div className="space-y-3 text-sm">
                <div className="flex justify-between">
                  <span className="text-gray-300">位置 X:</span>
                  <span className="text-white">{currentPose.x} m</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-300">位置 Y:</span>
                  <span className="text-white">{currentPose.y} m</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-300">方向角:</span>
                  <span className="text-white">{currentPose.theta}°</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-300">線速度:</span>
                  <span className="text-white">{linearVel} m/s</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-300">角速度:</span>
                  <span className="text-white">{angularVel} rad/s</span>
                </div>
              </div>
            </div>

            {/* 快速動作 */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h2 className="text-xl font-semibold text-white mb-4">快速動作</h2>

              <div className="grid grid-cols-1 gap-2">
                <button 
                  onClick={() => setNavigationGoal(0, 0)}
                  disabled={!rosConnected || currentMode !== 'navigation'}
                  className="flex items-center justify-center gap-2 px-3 py-2 bg-green-500 hover:bg-green-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                >
                  <MapPin className="w-4 h-4" />
                  回到原點
                </button>
                <button 
                  onClick={stopRobot}
                  disabled={!rosConnected}
                  className="flex items-center justify-center gap-2 px-3 py-2 bg-red-500 hover:bg-red-600 disabled:bg-gray-600 text-white rounded-lg text-sm transition-all"
                >
                  <Square className="w-4 h-4" />
                  緊急停止
                </button>
                <button 
                  onClick={() => window.open('http://localhost:8080', '_blank')}
                  className="flex items-center justify-center gap-2 px-3 py-2 bg-orange-500 hover:bg-orange-600 text-white rounded-lg text-sm transition-all"
                >
                  <Camera className="w-4 h-4" />
                  攝影機視圖
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TurtleBotInterface;
